  1→'use client';
  2→
  3→import { useState, useCallback, useEffect, useRef } from 'react';
  4→
  5→interface Paper {
  6→  id: string;
  7→  score: number;
  8→  corpusid: number;
  9→  title: string;
 10→  highlighted_title?: string | null;
 11→  year: number;
 12→  citationcount: number;
 13→  url: string;
 14→}
 15→
 16→interface SearchResult {
 17→  success: boolean;
 18→  query: string;
 19→  papers: Paper[];
 20→  options: {
 21→    use_time_decay: boolean;
 22→    use_boost: boolean;
 23→    highlight_mode?: string;
 24→    limit: number;
 25→  };
 26→}
 27→
 28→type HighlightMode = 'none' | 'lexical';
 29→
 30→function highlightText(
 31→  text: string,
 32→  query: string,
 33→  mode: HighlightMode
 34→): React.ReactNode {
 35→  if (mode === 'none' || !query.trim()) {
 36→    return text;
 37→  }
 38→
 39→  const words = query.toLowerCase().split(/\s+/).filter(Boolean);
 40→
 41→  if (mode === 'lexical') {
 42→    const regex = new RegExp(`(${words.map(w => w.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')).join('|')})`, 'gi');
 43→    const parts = text.split(regex);
 44→
 45→    return parts.map((part, i) => {
 46→      const isMatch = words.some(w => part.toLowerCase() === w);
 47→      if (isMatch) {
 48→        return (
 49→          <span key={i} className="bg-yellow-200/80 px-0.5 rounded-sm">
 50→            {part}
 51→          </span>
 52→        );
 53→      }
 54→      return part;
 55→    });
 56→  }
 57→
 58→  return text;
 59→}
 60→
 61→function SearchIcon({ className }: { className?: string }) {
 62→  return (
 63→    <svg className={className} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
 64→      <path fillRule="evenodd" d="M9 3.5a5.5 5.5 0 100 11 5.5 5.5 0 000-11zM2 9a7 7 0 1112.452 4.391l3.328 3.329a.75.75 0 11-1.06 1.06l-3.329-3.328A7 7 0 012 9z" clipRule="evenodd" />
 65→    </svg>
 66→  );
 67→}
 68→
 69→function ChevronIcon({ open, className }: { open: boolean; className?: string }) {
 70→  return (
 71→    <svg
 72→      className={`${className} transition-transform duration-200 ${open ? 'rotate-180' : ''}`}
 73→      xmlns="http://www.w3.org/2000/svg"
 74→      viewBox="0 0 20 20"
 75→      fill="currentColor"
 76→    >
 77→      <path fillRule="evenodd" d="M5.22 8.22a.75.75 0 0 1 1.06 0L10 11.94l3.72-3.72a.75.75 0 1 1 1.06 1.06l-4.25 4.25a.75.75 0 0 1-1.06 0L5.22 9.28a.75.75 0 0 1 0-1.06Z" clipRule="evenodd" />
 78→    </svg>
 79→  );
 80→}
 81→
 82→function LoadingSpinner() {
 83→  return (
 84→    <div className="flex flex-col items-center justify-center py-16 gap-4">
 85→      <div className="relative">
 86→        <div className="w-10 h-10 border-3 border-blue-200 rounded-full" />
 87→        <div className="absolute top-0 left-0 w-10 h-10 border-3 border-transparent border-t-blue-600 rounded-full animate-spin-slow" />
 88→      </div>
 89→      <div className="flex gap-1.5">
 90→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" />
 91→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.2s' }} />
 92→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.4s' }} />
 93→      </div>
 94→      <p className="text-sm text-slate-400 font-medium">Searching papers...</p>
 95→    </div>
 96→  );
 97→}
 98→
 99→function formatCitations(count: number): string {
100→  if (count >= 1000) {
101→    return `${(count / 1000).toFixed(count >= 10000 ? 0 : 1)}k`;
102→  }
103→  return count.toLocaleString();
104→}
105→
106→export default function Home() {
107→  const [query, setQuery] = useState('');
108→  const [results, setResults] = useState<SearchResult | null>(null);
109→  const [loading, setLoading] = useState(false);
110→  const [error, setError] = useState<string | null>(null);
111→
112→  // Options
113→  const [useTimeDecay, setUseTimeDecay] = useState(false);
114→  const [useBoost, setUseBoost] = useState(false);
115→  const [highlightMode, setHighlightMode] = useState<HighlightMode>('lexical');
116→  const [limit, setLimit] = useState(10);
117→  const [showOptions, setShowOptions] = useState(false);
118→
119→  // Autocomplete
120→  const [suggestions, setSuggestions] = useState<string[]>([]);
121→  const [showSuggestions, setShowSuggestions] = useState(false);
122→  const [selectedIdx, setSelectedIdx] = useState(-1);
123→  const debounceRef = useRef<ReturnType<typeof setTimeout> | null>(null);
124→  const inputRef = useRef<HTMLInputElement>(null);
125→
126→  useEffect(() => {
127→    if (debounceRef.current) clearTimeout(debounceRef.current);
128→    if (query.trim().length < 2) {
129→      setSuggestions([]);
130→      setShowSuggestions(false);
131→      return;
132→    }
133→    debounceRef.current = setTimeout(async () => {
134→      try {
135→        const res = await fetch('/api/autocomplete', {
136→          method: 'POST',
137→          headers: { 'Content-Type': 'application/json' },
138→          body: JSON.stringify({ query: query.trim(), limit: 5 }),
139→        });
140→        const data = await res.json();
141→        setSuggestions(data.titles || []);
142→        setShowSuggestions((data.titles || []).length > 0);
143→        setSelectedIdx(-1);
144→      } catch {
145→        setSuggestions([]);
146→      }
147→    }, 200);
148→    return () => { if (debounceRef.current) clearTimeout(debounceRef.current); };
149→  }, [query]);
150→
151→  // Advanced time decay parameters
152→  const [showAdvanced, setShowAdvanced] = useState(false);
153→  const [origin, setOrigin] = useState(2025);
154→  const [offset, setOffset] = useState(5);
155→  const [decay, setDecay] = useState(0.8);
156→  const [scale, setScale] = useState(8);
157→
158→  const handleSearch = useCallback(async () => {
159→    if (!query.trim()) return;
160→
161→    setLoading(true);
162→    setError(null);
163→
164→    try {
165→      const response = await fetch('/api/search', {
166→        method: 'POST',
167→        headers: {
168→          'Content-Type': 'application/json',
169→        },
170→        body: JSON.stringify({
171→          query: query.trim(),
172→          limit,
173→          use_time_decay: useTimeDecay,
174→          use_boost: useBoost,
175→          highlight_mode: highlightMode,
176→          time_decay_params: useTimeDecay ? { origin, offset, decay, scale } : undefined,
177→        }),
178→      });
179→
180→      if (!response.ok) {
181→        const errorData = await response.json();
182→        throw new Error(errorData.error || 'Search failed');
183→      }
184→
185→      const data = await response.json();
186→      setResults(data);
187→    } catch (err) {
188→      setError(err instanceof Error ? err.message : 'An error occurred');
189→    } finally {
190→      setLoading(false);
191→    }
192→  }, [query, limit, useTimeDecay, useBoost, highlightMode, origin, offset, decay, scale]);
193→
194→  const handleKeyDown = (e: React.KeyboardEvent) => {
195→    if (showSuggestions && suggestions.length > 0) {
196→      if (e.key === 'ArrowDown') {
197→        e.preventDefault();
198→        setSelectedIdx(prev => (prev + 1) % suggestions.length);
199→        return;
200→      }
201→      if (e.key === 'ArrowUp') {
202→        e.preventDefault();
203→        setSelectedIdx(prev => (prev - 1 + suggestions.length) % suggestions.length);
204→        return;
205→      }
206→      if (e.key === 'Enter' && selectedIdx >= 0) {
207→        e.preventDefault();
208→        setQuery(suggestions[selectedIdx]);
209→        setShowSuggestions(false);
210→        return;
211→      }
212→      if (e.key === 'Escape') {
213→        setShowSuggestions(false);
214→        return;
215→      }
216→    }
217→    if (e.key === 'Enter') {
218→      setShowSuggestions(false);
219→      handleSearch();
220→    }
221→  };
222→
223→  const activeOptionsCount = [useTimeDecay, useBoost, highlightMode !== 'none'].filter(Boolean).length;
224→
225→  return (
226→    <div className="min-h-screen bg-white">
227→      {/* Header */}
228→      <div className="bg-white border-b border-slate-200">
229→        <div className="max-w-6xl mx-auto px-4 pt-8 pb-8">
230→          <div className="text-center mb-6">
231→            <h1 className="text-3xl font-semibold tracking-tight text-slate-800 mb-2">
232→              Paper Search
233→            </h1>
234→            <p className="text-slate-500 text-sm">
235→              Semantic paper search powered by Milvus v2.6
236→            </p>
237→          </div>
238→
239→          {/* Search Box */}
240→          <div className="relative max-w-2xl mx-auto">
241→            <div className="relative">
242→              <SearchIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
243→              <input
244→                ref={inputRef}
245→                type="text"
246→                value={query}
247→                onChange={(e) => setQuery(e.target.value)}
248→                onKeyDown={handleKeyDown}
249→                onFocus={() => suggestions.length > 0 && setShowSuggestions(true)}
250→                onBlur={() => setTimeout(() => setShowSuggestions(false), 150)}
251→                placeholder="Search for papers..."
252→                className="w-full pl-12 pr-28 py-3.5 bg-white border border-slate-200 rounded-lg text-slate-700 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600 text-base"
253→                autoComplete="off"
254→              />
255→              <button
256→                onClick={handleSearch}
257→                disabled={loading || !query.trim()}
258→                className="absolute right-2 top-1/2 -translate-y-1/2 px-5 py-2 bg-blue-600 text-white rounded-lg font-medium text-sm hover:bg-blue-700 disabled:bg-slate-200 disabled:text-slate-400 disabled:cursor-not-allowed"
259→              >
260→                Search
261→              </button>
262→            </div>
263→
264→            {/* Autocomplete dropdown */}
265→            {showSuggestions && suggestions.length > 0 && (
266→              <ul className="absolute z-20 left-0 right-0 mt-1 bg-white border border-slate-200 rounded-lg shadow-lg overflow-hidden custom-scrollbar max-h-64 overflow-auto">
267→                {suggestions.map((title, i) => {
268→                  const q = query.trim();
269→                  const idx = title.indexOf(q);
270→                  return (
271→                    <li
272→                      key={i}
273→                      className={`px-4 py-3 text-sm cursor-pointer border-b border-slate-100 last:border-0 flex items-center gap-3 ${
274→                        i === selectedIdx
275→                          ? 'bg-blue-50 text-blue-900'
276→                          : 'text-slate-700 hover:bg-slate-50'
277→                      }`}
278→                      onMouseDown={() => {
279→                        setQuery(title);
280→                        setShowSuggestions(false);
281→                      }}
282→                    >
283→                      <SearchIcon className={`w-3.5 h-3.5 flex-shrink-0 ${i === selectedIdx ? 'text-blue-400' : 'text-slate-300'}`} />
284→                      <span className="truncate">
285→                        {idx >= 0 ? (
286→                          <>
287→                            {title.slice(0, idx)}
288→                            <strong className="font-semibold text-blue-600">{title.slice(idx, idx + q.length)}</strong>
289→                            {title.slice(idx + q.length)}
290→                          </>
291→                        ) : title}
292→                      </span>
293→                    </li>
294→                  );
295→                })}
296→              </ul>
297→            )}
298→          </div>
299→        </div>
300→      </div>
301→
302→      {/* Two-column layout: sidebar + main content */}
303→      <div className="max-w-6xl mx-auto px-4 py-6">
304→        <div className="flex flex-col md:flex-row gap-6">
305→          {/* Left sidebar — collapses to horizontal on mobile */}
306→          <aside className="w-full md:w-64 flex-shrink-0">
307→            <div className="bg-white rounded-lg border border-slate-200 overflow-hidden">
308→              <button
309→                onClick={() => setShowOptions(!showOptions)}
310→                className="w-full flex items-center justify-between px-4 py-3 hover:bg-slate-50 text-left"
311→              >
312→                <div className="flex items-center gap-2">
313→                  <svg className="w-4 h-4 text-slate-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
314→                    <path d="M17 2.75a.75.75 0 0 0-1.5 0v5.5a.75.75 0 0 0 1.5 0v-5.5ZM17 15.75a.75.75 0 0 0-1.5 0v1.5a.75.75 0 0 0 1.5 0v-1.5ZM3.75 15a.75.75 0 0 1 .75.75v1.5a.75.75 0 0 1-1.5 0v-1.5a.75.75 0 0 1 .75-.75ZM4.5 2.75a.75.75 0 0 0-1.5 0v5.5a.75.75 0 0 0 1.5 0v-5.5ZM10 11a.75.75 0 0 1 .75.75v5.5a.75.75 0 0 1-1.5 0v-5.5A.75.75 0 0 1 10 11ZM10.75 2.75a.75.75 0 0 0-1.5 0v1.5a.75.75 0 0 0 1.5 0v-1.5ZM10 5a2 2 0 1 0 0 4 2 2 0 0 0 0-4ZM3.75 10a2 2 0 1 0 0 4 2 2 0 0 0 0-4ZM16.25 10a2 2 0 1 0 0 4 2 2 0 0 0 0-4Z" />
315→                  </svg>
316→                  <span className="text-sm font-medium text-slate-700">Search Options</span>
317→                  {activeOptionsCount > 0 && (
318→                    <span className="text-xs bg-blue-100 text-blue-700 px-2 py-0.5 rounded-full font-medium">
319→                      {activeOptionsCount}
320→                    </span>
321→                  )}
322→                </div>
323→                <ChevronIcon open={showOptions} className="w-4 h-4 text-slate-400" />
324→              </button>
325→
326→              {showOptions && (
327→                <div className="px-4 pb-4 border-t border-slate-100">
328→                  <div className="space-y-3 pt-3">
329→                    {/* Rankers */}
330→                    <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
331→                      <input
332→                        type="checkbox"
333→                        checked={useTimeDecay}
334→                        onChange={(e) => setUseTimeDecay(e.target.checked)}
335→                        className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
336→                      />
337→                      <div>
338→                        <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Time Decay</span>
339→                        <p className="text-xs text-slate-400 mt-0.5">Prefer recent papers</p>
340→                      </div>
341→                    </label>
342→
343→                    <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
344→                      <input
345→                        type="checkbox"
346→                        checked={useBoost}
347→                        onChange={(e) => setUseBoost(e.target.checked)}
348→                        className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
349→                      />
350→                      <div>
351→                        <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Citation Boost</span>
352→                        <p className="text-xs text-slate-400 mt-0.5">Boost highly-cited papers</p>
353→                      </div>
354→                    </label>
355→                  </div>
356→
357→                  {/* Highlighting & Limit */}
358→                  <div className="space-y-3 mt-3 pt-3 border-t border-slate-100">
359→                    <div>
360→                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Highlight</span>
361→                      <div className="flex bg-slate-100 rounded-lg p-0.5">
362→                        <button
363→                          onClick={() => setHighlightMode('none')}
364→                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
365→                            highlightMode === 'none'
366→                              ? 'bg-white text-slate-700 shadow-sm'
367→                              : 'text-slate-500 hover:text-slate-700'
368→                          }`}
369→                        >
370→                          Off
371→                        </button>
372→                        <button
373→                          onClick={() => setHighlightMode('lexical')}
374→                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
375→                            highlightMode === 'lexical'
376→                              ? 'bg-white text-slate-700 shadow-sm'
377→                              : 'text-slate-500 hover:text-slate-700'
378→                          }`}
379→                        >
380→                          Lexical
381→                        </button>
382→                      </div>
383→                    </div>
384→
385→                    <div>
386→                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Results</span>
387→                      <select
388→                        value={limit}
389→                        onChange={(e) => setLimit(Number(e.target.value))}
390→                        className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
391→                      >
392→                        {[5, 10, 15, 20, 25, 50].map(n => (
393→                          <option key={n} value={n}>{n}</option>
394→                        ))}
395→                      </select>
396→                    </div>
397→                  </div>
398→
399→                  {/* Advanced Options */}
400→                  {useTimeDecay && (
401→                    <div className="mt-3 pt-3 border-t border-slate-100">
402→                      <button
403→                        onClick={() => setShowAdvanced(!showAdvanced)}
404→                        className="text-xs text-blue-600 hover:text-blue-700 font-medium flex items-center gap-1"
405→                      >
406→                        <ChevronIcon open={showAdvanced} className="w-3 h-3" />
407→                        Advanced Settings
408→                      </button>
409→
410→                      {showAdvanced && (
411→                        <div className="grid grid-cols-2 gap-2 mt-2 bg-slate-50 p-3 rounded-lg">
412→                          <div>
413→                            <label className="block text-xs text-slate-500 mb-1 font-medium">Origin</label>
414→                            <input
415→                              type="number"
416→                              value={origin}
417→                              onChange={(e) => setOrigin(Number(e.target.value))}
418→                              className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
419→                            />
420→                          </div>
421→                          <div>
422→                            <label className="block text-xs text-slate-500 mb-1 font-medium">Offset</label>
423→                            <input
424→                              type="number"
425→                              value={offset}
426→                              onChange={(e) => setOffset(Number(e.target.value))}
427→                              className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
428→                            />
429→                          </div>
430→                          <div>
431→                            <label className="block text-xs text-slate-500 mb-1 font-medium">Decay</label>
432→                            <input
433→                              type="number"
434→                              step="0.1"
435→                              min="0"
436→                              max="1"
437→                              value={decay}
438→                              onChange={(e) => setDecay(Number(e.target.value))}
439→                              className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
440→                            />
441→                          </div>
442→                          <div>
443→                            <label className="block text-xs text-slate-500 mb-1 font-medium">Scale</label>
444→                            <input
445→                              type="number"
446→                              value={scale}
447→                              onChange={(e) => setScale(Number(e.target.value))}
448→                              className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
449→                            />
450→                          </div>
451→                        </div>
452→                      )}
453→                    </div>
454→                  )}
455→                </div>
456→              )}
457→            </div>
458→          </aside>
459→
460→          {/* Main content area */}
461→          <main className="flex-1 min-w-0">
462→            {/* Error Display */}
463→            {error && (
464→              <div className="flex items-start gap-3 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
465→                <svg className="w-5 h-5 text-red-400 flex-shrink-0 mt-0.5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
466→                  <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-8-5a.75.75 0 01.75.75v4.5a.75.75 0 01-1.5 0v-4.5A.75.75 0 0110 5zm0 10a1 1 0 100-2 1 1 0 000 2z" clipRule="evenodd" />
467→                </svg>
468→                <p className="text-sm">{error}</p>
469→              </div>
470→            )}
471→
472→            {/* Loading */}
473→            {loading && <LoadingSpinner />}
474→
475→            {/* Results */}
476→            {!loading && results && (
477→              <div className="pb-12">
478→                <div className="flex items-center justify-between mb-5">
479→                  <div className="flex items-baseline gap-2">
480→                    <h2 className="text-lg font-semibold text-slate-700">
481→                      Results
482→                    </h2>
483→                    <span className="text-sm text-slate-400">
484→                      {results.papers.length} paper{results.papers.length !== 1 ? 's' : ''} for &ldquo;{results.query}&rdquo;
485→                    </span>
486→                  </div>
487→                  <div className="flex gap-1.5">
488→                    {results.options.use_time_decay && (
489→                      <span className="inline-flex items-center gap-1 text-xs font-medium bg-violet-50 text-violet-700 border border-violet-200 px-2.5 py-1 rounded-full">
490→                        <svg className="w-3 h-3" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
491→                          <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm.75-13a.75.75 0 00-1.5 0v5c0 .414.336.75.75.75h4a.75.75 0 000-1.5h-3.25V5z" clipRule="evenodd" />
492→                        </svg>
493→                        Time Decay
494→                      </span>
495→                    )}
496→                    {results.options.use_boost && (
