file.content:
@import "tailwindcss";

:root {
  --background: #ffffff;
  --foreground: #334155;
  --primary: #2563eb;
  --primary-dark: #1d4ed8;
  --primary-light: #dbeafe;
  --surface: #ffffff;
  --surface-hover: #f8fafc;
  --border: #e2e8f0;
  --border-light: #f1f5f9;
  --text-primary: #334155;
  --text-secondary: #475569;
  --text-muted: #94a3b8;
  --accent-green: #059669;
  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.07), 0 2px 4px -2px rgb(0 0 0 / 0.05);
  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.08), 0 4px 6px -4px rgb(0 0 0 / 0.04);
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
}

body {
  background: var(--background);
  color: var(--foreground);
  font-family: var(--font-geist-sans), system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Typography hierarchy */
h1, h2, h3 {
  font-weight: 600;
  color: #334155;
}

label, .label {
  font-weight: 500;
}

/* Custom focus styles */
input:focus,
select:focus,
button:focus {
  outline: none;
}

/* Smooth transitions for interactive elements (200ms) */
a, button, input, select, label,
.transition-all {
  transition-property: color, background-color, border-color, box-shadow, transform, opacity;
  transition-duration: 200ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

/* Server-side highlighting styles */
mark.lexical {
  background-color: #fef08a;
  padding: 0 2px;
  border-radius: 3px;
  box-decoration-break: clone;
}

mark.semantic {
  background-color: #bbf7d0;
  padding: 0 2px;
  border-radius: 3px;
  box-decoration-break: clone;
}

/* Search result card entrance animation */
@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(12px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-slide-up {
  animation: slideUp 0.35s cubic-bezier(0.4, 0, 0.2, 1) both;
}

/* Loading spinner */
@keyframes spin-slow {
  to {
    transform: rotate(360deg);
  }
}

.animate-spin-slow {
  animation: spin-slow 1.2s linear infinite;
}

/* Pulse animation for loading dots */
@keyframes pulse-dot {
  0%, 80%, 100% {
    transform: scale(0.6);
    opacity: 0.4;
  }
  40% {
    transform: scale(1);
    opacity: 1;
  }
}

.animate-pulse-dot {
  animation: pulse-dot 1.4s ease-in-out infinite;
}

/* Custom scrollbar for autocomplete */
.custom-scrollbar::-webkit-scrollbar {
  width: 6px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: transparent;
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background-color: #cbd5e1;
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background-color: #94a3b8;
}

input.old_string:
{/* Header */}
<div className="bg-white border-b border-slate-200">
  <div className="max-w-6xl mx-auto px-4 pt-8 pb-8">
    <div className="text-center mb-6">
      <h1 className="text-3xl font-semibold tracking-tight text-slate-800 mb-2">
        Paper Search
      </h1>
      <p className="text-slate-500 text-sm">
        Semantic paper search powered by Milvus v2.6
      </p>
    </div>

    {/* Search Box */}
    <div className="relative max-w-2xl mx-auto">
      <div className="relative">
        <SearchIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
        <input
          ref={inputRef}
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={handleKeyDown}
          onFocus={() => suggestions.length > 0 && setShowSuggestions(true)}
          onBlur={() => setTimeout(() => setShowSuggestions(false), 150)}
          placeholder="Search for papers..."
          className="w-full pl-12 pr-28 py-3.5 bg-white border border-slate-200 rounded-lg text-slate-700 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600 text-base"
          autoComplete="off"
        />
        <button
          onClick={handleSearch}
          disabled={loading || !query.trim()}
          className="absolute right-2 top-1/2 -translate-y-1/2 px-5 py-2 bg-blue-600 text-white rounded-lg font-medium text-sm hover:bg-blue-700 disabled:bg-slate-200 disabled:text-slate-400 disabled:cursor-not-allowed"
        >
          Search
        </button>
      </div>

      {/* Autocomplete dropdown */}
      {showSuggestions && suggestions.length > 0 && (
        <ul className="absolute z-20 left-0 right-0 mt-1 bg-white border border-slate-200 rounded-lg shadow-lg overflow-hidden custom-scrollbar max-h-64 overflow-auto">
          {suggestions.map((title, i) => {
            const q = query.trim();
            const idx = title.indexOf(q);
            return (
              <li
                key={i}
                className={`px-4 py-3 text-sm cursor-pointer border-b border-slate-100 last:border-0 flex items-center gap-3 ${
                  i === selectedIdx
                    ? 'bg-blue-50 text-blue-900'
                    : 'text-slate-700 hover:bg-slate-50'
                }`}
                onMouseDown={() => {
                  setQuery(title);
                  setShowSuggestions(false);
                }}
              >
                <SearchIcon className={`w-3.5 h-3.5 flex-shrink-0 ${i === selectedIdx ? 'text-blue-400' : 'text-slate-300'}`} />
                <span className="truncate">
                  {idx >= 0 ? (
                    <>
                      {title.slice(0, idx)}
                      <strong className="font-semibold text-blue-600">{title.slice(idx, idx + q.length)}</strong>
                      {title.slice(idx + q.length)}
                    </>
                  ) : title}
                </span>
              </li>
            );
          })}
        </ul>
      )}
    </div>
  </div>
</div>

