  1→'use client';
  2→
  3→import { useState, useCallback, useEffect, useRef } from 'react';
  4→
  5→interface Paper {
  6→  id: string;
  7→  score: number;
  8→  corpusid: number;
  9→  title: string;
 10→  highlighted_title?: string | null;
 11→  year: number;
 12→  citationcount: number;
 13→  url: string;
 14→}
 15→
 16→interface SearchResult {
 17→  success: boolean;
 18→  query: string;
 19→  papers: Paper[];
 20→  options: {
 21→    use_time_decay: boolean;
 22→    use_boost: boolean;
 23→    use_boost_ranker?: boolean;
 24→    highlight_mode?: string;
 25→    limit: number;
 26→  };
 27→}
 28→
 29→type HighlightMode = 'none' | 'lexical';
 30→type SearchMode = 'semantic' | 'keyword' | 'hybrid';
 31→
 32→function highlightText(
 33→  text: string,
 34→  query: string,
 35→  mode: HighlightMode
 36→): React.ReactNode {
 37→  if (mode === 'none' || !query.trim()) {
 38→    return text;
 39→  }
 40→
 41→  const words = query.toLowerCase().split(/\s+/).filter(Boolean);
 42→
 43→  if (mode === 'lexical') {
 44→    const regex = new RegExp(`(${words.map(w => w.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')).join('|')})`, 'gi');
 45→    const parts = text.split(regex);
 46→
 47→    return parts.map((part, i) => {
 48→      const isMatch = words.some(w => part.toLowerCase() === w);
 49→      if (isMatch) {
 50→        return (
 51→          <span key={i} className="bg-yellow-200/80 px-0.5 rounded-sm">
 52→            {part}
 53→          </span>
 54→        );
 55→      }
 56→      return part;
 57→    });
 58→  }
 59→
 60→  return text;
 61→}
 62→
 63→function SearchIcon({ className }: { className?: string }) {
 64→  return (
 65→    <svg className={className} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
 66→      <path fillRule="evenodd" d="M9 3.5a5.5 5.5 0 100 11 5.5 5.5 0 000-11zM2 9a7 7 0 1112.452 4.391l3.328 3.329a.75.75 0 11-1.06 1.06l-3.329-3.328A7 7 0 012 9z" clipRule="evenodd" />
 67→    </svg>
 68→  );
 69→}
 70→
 71→function ChevronIcon({ open, className }: { open: boolean; className?: string }) {
 72→  return (
 73→    <svg
 74→      className={`${className} transition-transform duration-200 ${open ? 'rotate-180' : ''}`}
 75→      xmlns="http://www.w3.org/2000/svg"
 76→      viewBox="0 0 20 20"
 77→      fill="currentColor"
 78→    >
 79→      <path fillRule="evenodd" d="M5.22 8.22a.75.75 0 0 1 1.06 0L10 11.94l3.72-3.72a.75.75 0 1 1 1.06 1.06l-4.25 4.25a.75.75 0 0 1-1.06 0L5.22 9.28a.75.75 0 0 1 0-1.06Z" clipRule="evenodd" />
 80→    </svg>
 81→  );
 82→}
 83→
 84→function LoadingSpinner() {
 85→  return (
 86→    <div className="flex flex-col items-center justify-center py-16 gap-4">
 87→      <div className="relative">
 88→        <div className="w-10 h-10 border-3 border-blue-200 rounded-full" />
 89→        <div className="absolute top-0 left-0 w-10 h-10 border-3 border-transparent border-t-blue-600 rounded-full animate-spin-slow" />
 90→      </div>
 91→      <div className="flex gap-1.5">
 92→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" />
 93→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.2s' }} />
 94→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.4s' }} />
 95→      </div>
 96→      <p className="text-sm text-slate-400 font-medium">Searching papers...</p>
 97→    </div>
 98→  );
 99→}
100→
101→function formatCitations(count: number): string {
102→  if (count >= 1000) {
103→    return `${(count / 1000).toFixed(count >= 10000 ? 0 : 1)}k`;
104→  }
105→  return count.toLocaleString();
106→}
107→
108→export default function Home() {
109→  const [query, setQuery] = useState('');
110→  const [results, setResults] = useState<SearchResult | null>(null);
111→  const [loading, setLoading] = useState(false);
112→  const [error, setError] = useState<string | null>(null);
113→
114→  // Search mode
115→  const [searchMode, setSearchMode] = useState<SearchMode>('hybrid');
116→
117→  // Options
118→  const [useTimeDecay, setUseTimeDecay] = useState(false);
119→  const [useBoost, setUseBoost] = useState(false);
120→  const [useBoostRanker, setUseBoostRanker] = useState(false);
121→  const [highlightMode, setHighlightMode] = useState<HighlightMode>('lexical');
122→  const [limit, setLimit] = useState(10);
123→  const [showSettings, setShowSettings] = useState(false);
124→  const settingsRef = useRef<HTMLDivElement>(null);
125→
126→  // Filters
127→  const [yearMin, setYearMin] = useState<string>('');
128→  const [yearMax, setYearMax] = useState<string>('');
129→  const [citationMin, setCitationMin] = useState<string>('');
130→
131→  // Autocomplete
132→  const [suggestions, setSuggestions] = useState<string[]>([]);
133→  const [showSuggestions, setShowSuggestions] = useState(false);
134→  const [selectedIdx, setSelectedIdx] = useState(-1);
135→  const debounceRef = useRef<ReturnType<typeof setTimeout> | null>(null);
136→  const inputRef = useRef<HTMLInputElement>(null);
137→
138→  useEffect(() => {
139→    if (debounceRef.current) clearTimeout(debounceRef.current);
140→    if (query.trim().length < 2) {
141→      setSuggestions([]);
142→      setShowSuggestions(false);
143→      return;
144→    }
145→    debounceRef.current = setTimeout(async () => {
146→      try {
147→        const res = await fetch('/api/autocomplete', {
148→          method: 'POST',
149→          headers: { 'Content-Type': 'application/json' },
150→          body: JSON.stringify({ query: query.trim(), limit: 5 }),
151→        });
152→        const data = await res.json();
153→        setSuggestions(data.titles || []);
154→        setShowSuggestions((data.titles || []).length > 0);
155→        setSelectedIdx(-1);
156→      } catch {
157→        setSuggestions([]);
158→      }
159→    }, 200);
160→    return () => { if (debounceRef.current) clearTimeout(debounceRef.current); };
161→  }, [query]);
162→
163→  // Close settings popover on click outside
164→  useEffect(() => {
165→    function handleClickOutside(e: MouseEvent) {
166→      if (settingsRef.current && !settingsRef.current.contains(e.target as Node)) {
167→        setShowSettings(false);
168→      }
169→    }
170→    if (showSettings) {
171→      document.addEventListener('mousedown', handleClickOutside);
172→      return () => document.removeEventListener('mousedown', handleClickOutside);
173→    }
174→  }, [showSettings]);
175→
176→  // Advanced time decay parameters
177→  const [showAdvanced, setShowAdvanced] = useState(false);
178→  const [origin, setOrigin] = useState(2025);
179→  const [offset, setOffset] = useState(5);
180→  const [decay, setDecay] = useState(0.8);
181→  const [scale, setScale] = useState(8);
182→
183→  // Build Milvus filter expression from filter state
184→  const buildFilterExpr = useCallback(() => {
185→    const parts: string[] = [];
186→    if (yearMin) parts.push(`year >= ${yearMin}`);
187→    if (yearMax) parts.push(`year <= ${yearMax}`);
188→    if (citationMin) parts.push(`citationcount >= ${citationMin}`);
189→    return parts.join(' and ');
190→  }, [yearMin, yearMax, citationMin]);
191→
192→  const handleSearch = useCallback(async () => {
193→    if (!query.trim()) return;
194→
195→    setLoading(true);
196→    setError(null);
197→
198→    try {
199→      const filterExpr = buildFilterExpr();
200→      const response = await fetch('/api/search', {
201→        method: 'POST',
202→        headers: {
203→          'Content-Type': 'application/json',
204→        },
205→        body: JSON.stringify({
206→          query: query.trim(),
207→          limit,
208→          search_mode: searchMode,
209→          use_time_decay: useTimeDecay,
210→          use_boost: useBoost,
211→          use_boost_ranker: useBoostRanker,
212→          highlight_mode: highlightMode,
213→          filter: filterExpr || undefined,
214→          time_decay_params: useTimeDecay ? { origin, offset, decay, scale } : undefined,
215→        }),
216→      });
217→
218→      if (!response.ok) {
219→        const errorData = await response.json();
220→        throw new Error(errorData.error || 'Search failed');
221→      }
222→
223→      const data = await response.json();
224→      setResults(data);
225→    } catch (err) {
226→      setError(err instanceof Error ? err.message : 'An error occurred');
227→    } finally {
228→      setLoading(false);
229→    }
230→  }, [query, limit, searchMode, useTimeDecay, useBoost, useBoostRanker, highlightMode, origin, offset, decay, scale, buildFilterExpr]);
231→
232→  const handleKeyDown = (e: React.KeyboardEvent) => {
233→    if (showSuggestions && suggestions.length > 0) {
234→      if (e.key === 'ArrowDown') {
235→        e.preventDefault();
236→        setSelectedIdx(prev => (prev + 1) % suggestions.length);
237→        return;
238→      }
239→      if (e.key === 'ArrowUp') {
240→        e.preventDefault();
241→        setSelectedIdx(prev => (prev - 1 + suggestions.length) % suggestions.length);
242→        return;
243→      }
244→      if (e.key === 'Enter' && selectedIdx >= 0) {
245→        e.preventDefault();
246→        setQuery(suggestions[selectedIdx]);
247→        setShowSuggestions(false);
248→        return;
249→      }
250→      if (e.key === 'Escape') {
251→        setShowSuggestions(false);
252→        return;
253→      }
254→    }
255→    if (e.key === 'Enter') {
256→      setShowSuggestions(false);
257→      handleSearch();
258→    }
259→  };
260→
261→  const handleSearchModeChange = useCallback((mode: SearchMode) => {
262→    setSearchMode(mode);
263→  }, []);
264→
265→  // Re-execute search when searchMode changes (if a query exists)
266→  const prevSearchModeRef = useRef(searchMode);
267→  useEffect(() => {
268→    if (prevSearchModeRef.current !== searchMode && query.trim()) {
269→      prevSearchModeRef.current = searchMode;
270→      handleSearch();
271→    } else {
272→      prevSearchModeRef.current = searchMode;
273→    }
274→  }, [searchMode, query, handleSearch]);
275→
276→  // Re-execute search when filters change via chip removal (if a query exists and results are showing)
277→  const filterExpr = buildFilterExpr();
278→  const prevFilterRef = useRef(filterExpr);
279→  const hasResultsRef = useRef(false);
280→  hasResultsRef.current = results !== null;
281→  useEffect(() => {
282→    if (prevFilterRef.current !== filterExpr && query.trim() && hasResultsRef.current) {
283→      prevFilterRef.current = filterExpr;
284→      handleSearch();
285→    } else {
286→      prevFilterRef.current = filterExpr;
287→    }
288→  }, [filterExpr, query, handleSearch]);
289→
290→  // Build active settings summary
291→  const settingsSummary: string[] = [];
292→  settingsSummary.push(`${limit} results`);
293→  if (useBoostRanker) settingsSummary.push('Boost Ranker');
294→  if (useTimeDecay) settingsSummary.push('Time Decay');
295→  if (useBoost) settingsSummary.push('Citation Boost');
296→  if (highlightMode === 'lexical') settingsSummary.push('Keyword highlights');
297→
298→  return (
299→    <div className="min-h-screen bg-white">
300→      {/* Header */}
301→      <div className="bg-white border-b border-slate-200">
302→        <div className="max-w-6xl mx-auto px-4 pt-10 pb-8">
303→          <div className="text-center mb-8">
304→            <h1 className="text-3xl font-semibold tracking-tight text-slate-800 mb-1.5">
305→              Paper Search
306→            </h1>
307→            <p className="text-slate-500 text-sm">
308→              Semantic paper search powered by Milvus v2.6
309→            </p>
310→          </div>
311→
312→          {/* Search Mode Switcher + Settings Gear */}
313→          <div className="flex justify-center items-center gap-2 mb-6">
314→            <div className="inline-flex bg-slate-100 rounded-lg p-1 gap-1">
315→              {([
316→                { mode: 'semantic' as SearchMode, label: 'Semantic', subtitle: 'Conceptual similarity' },
317→                { mode: 'keyword' as SearchMode, label: 'Keyword', subtitle: 'Exact term matching' },
318→                { mode: 'hybrid' as SearchMode, label: 'Hybrid', subtitle: 'Combined search' },
319→              ]).map(({ mode, label, subtitle }) => (
320→                <button
321→                  key={mode}
322→                  onClick={() => handleSearchModeChange(mode)}
323→                  className={`px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
324→                    searchMode === mode
325→                      ? 'bg-blue-600 text-white shadow-sm'
326→                      : 'text-slate-600 hover:text-slate-800 hover:bg-slate-200'
327→                  }`}
328→                >
329→                  <span className="block">{label}</span>
330→                  <span className={`block text-[10px] font-normal mt-0.5 ${
331→                    searchMode === mode ? 'text-blue-100' : 'text-slate-400'
332→                  }`}>
333→                    {subtitle}
334→                  </span>
335→                </button>
336→              ))}
337→            </div>
338→
339→            {/* Settings Gear Button */}
340→            <div ref={settingsRef} className="relative">
341→              <button
342→                onClick={() => setShowSettings(!showSettings)}
343→                className={`p-2.5 rounded-lg border transition-colors duration-200 ${
344→                  showSettings
345→                    ? 'bg-slate-100 border-slate-300 text-slate-700'
346→                    : 'bg-white border-slate-200 text-slate-400 hover:text-slate-600 hover:border-slate-300'
347→                }`}
348→                title="Search settings"
349→              >
350→                <svg className="w-5 h-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
351→                  <path fillRule="evenodd" d="M7.84 1.804A1 1 0 018.82 1h2.36a1 1 0 01.98.804l.331 1.652a6.993 6.993 0 011.929 1.115l1.598-.54a1 1 0 011.186.447l1.18 2.044a1 1 0 01-.205 1.251l-1.267 1.113a7.047 7.047 0 010 2.228l1.267 1.113a1 1 0 01.206 1.25l-1.18 2.045a1 1 0 01-1.187.447l-1.598-.54a6.993 6.993 0 01-1.929 1.115l-.33 1.652a1 1 0 01-.98.804H8.82a1 1 0 01-.98-.804l-.331-1.652a6.993 6.993 0 01-1.929-1.115l-1.598.54a1 1 0 01-1.186-.447l-1.18-2.044a1 1 0 01.205-1.251l1.267-1.114a7.05 7.05 0 010-2.227L1.821 7.773a1 1 0 01-.206-1.25l1.18-2.045a1 1 0 011.187-.447l1.598.54A6.993 6.993 0 017.51 3.456l.33-1.652zM10 13a3 3 0 100-6 3 3 0 000 6z" clipRule="evenodd" />
352→                </svg>
353→              </button>
354→
355→              {/* Settings Popover */}
356→              {showSettings && (
357→                <div className="absolute right-0 top-full mt-2 w-72 bg-white rounded-lg border border-slate-200 shadow-lg z-30 overflow-hidden">
358→                  <div className="px-4 py-3 border-b border-slate-100">
359→                    <span className="text-sm font-semibold text-slate-700">Search Settings</span>
360→                  </div>
361→                  <div className="px-4 py-3 space-y-3">
362→                    {/* Ranker Toggles */}
363→                    <div>
364→                      <span className="text-xs font-medium text-slate-500 mb-2 block">Rankers</span>
365→                      <div className="space-y-1">
366→                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
367→                          <input
368→                            type="checkbox"
369→                            checked={useTimeDecay}
370→                            onChange={(e) => setUseTimeDecay(e.target.checked)}
371→                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
372→                          />
373→                          <div>
374→                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Time Decay</span>
375→                            <p className="text-xs text-slate-400 mt-0.5">Prefer recent papers</p>
376→                          </div>
377→                        </label>
378→
379→                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
380→                          <input
381→                            type="checkbox"
382→                            checked={useBoost}
383→                            onChange={(e) => setUseBoost(e.target.checked)}
384→                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
385→                          />
386→                          <div>
387→                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Citation Boost</span>
388→                            <p className="text-xs text-slate-400 mt-0.5">Boost highly-cited papers</p>
389→                          </div>
390→                        </label>
391→
392→                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
393→                          <input
394→                            type="checkbox"
395→                            checked={useBoostRanker}
396→                            onChange={(e) => setUseBoostRanker(e.target.checked)}
397→                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
398→                          />
399→                          <div>
400→                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Boost Ranker <span className="text-[10px] font-normal text-blue-600">v2.6</span></span>
401→                            <p className="text-xs text-slate-400 mt-0.5">Recency + citation boosting</p>
402→                          </div>
403→                        </label>
404→                      </div>
405→                    </div>
406→
407→                    {/* Highlighting */}
408→                    <div className="pt-2 border-t border-slate-100">
409→                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Highlighting</span>
410→                      <div className="flex bg-slate-100 rounded-lg p-0.5">
411→                        <button
412→                          onClick={() => setHighlightMode('none')}
413→                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
414→                            highlightMode === 'none'
415→                              ? 'bg-white text-slate-700 shadow-sm'
416→                              : 'text-slate-500 hover:text-slate-700'
417→                          }`}
418→                        >
419→                          Off
420→                        </button>
421→                        <button
422→                          onClick={() => setHighlightMode('lexical')}
423→                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
424→                            highlightMode === 'lexical'
425→                              ? 'bg-white text-slate-700 shadow-sm'
426→                              : 'text-slate-500 hover:text-slate-700'
427→                          }`}
428→                        >
429→                          Keyword Highlights
430→                        </button>
431→                      </div>
432→                    </div>
433→
434→                    {/* Result Limit */}
435→                    <div className="pt-2 border-t border-slate-100">
436→                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Results per search</span>
437→                      <select
438→                        value={limit}
439→                        onChange={(e) => setLimit(Number(e.target.value))}
440→                        className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
441→                      >
442→                        {[5, 10, 15, 20, 25, 50].map(n => (
443→                          <option key={n} value={n}>{n}</option>
444→                        ))}
445→                      </select>
446→                    </div>
447→
448→                    {/* Advanced Time Decay Settings */}
449→                    {useTimeDecay && (
450→                      <div className="pt-2 border-t border-slate-100">
451→                        <button
452→                          onClick={() => setShowAdvanced(!showAdvanced)}
453→                          className="text-xs text-blue-600 hover:text-blue-700 font-medium flex items-center gap-1"
454→                        >
455→                          <ChevronIcon open={showAdvanced} className="w-3 h-3" />
456→                          Advanced Settings
457→                        </button>
458→
459→                        {showAdvanced && (
460→                          <div className="grid grid-cols-2 gap-2 mt-2 bg-slate-50 p-3 rounded-lg">
461→                            <div>
462→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Origin</label>
463→                              <input
464→                                type="number"
465→                                value={origin}
466→                                onChange={(e) => setOrigin(Number(e.target.value))}
467→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
468→                              />
469→                            </div>
470→                            <div>
471→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Offset</label>
472→                              <input
473→                                type="number"
474→                                value={offset}
475→                                onChange={(e) => setOffset(Number(e.target.value))}
476→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
477→                              />
478→                            </div>
479→                            <div>
480→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Decay</label>
481→                              <input
482→                                type="number"
483→                                step="0.1"
484→                                min="0"
485→                                max="1"
486→                                value={decay}
487→                                onChange={(e) => setDecay(Number(e.target.value))}
488→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
489→                              />
490→                            </div>
491→                            <div>
492→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Scale</label>
493→                              <input
494→                                type="number"
495→                                value={scale}
496→                                onChange={(e) => setScale(Number(e.target.value))}
497→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
498→                              />
499→                            </div>
500→                          </div>
501→                        )}
502→                      </div>
503→                    )}
504→                  </div>
505→                </div>
506→              )}
507→            </div>
508→          </div>
509→
510→          {/* Search Box */}
511→          <div className="relative max-w-2xl mx-auto">
512→            <div className="relative">
513→              <SearchIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400 pointer-events-none" />
514→              <input
515→                ref={inputRef}
516→                type="text"
517→                value={query}
518→                onChange={(e) => setQuery(e.target.value)}
519→                onKeyDown={handleKeyDown}
520→                onFocus={() => suggestions.length > 0 && setShowSuggestions(true)}
521→                onBlur={() => setTimeout(() => setShowSuggestions(false), 150)}
522→                placeholder="Search for papers..."
