  1→'use client';
  2→
  3→import { useState, useCallback, useEffect, useRef } from 'react';
  4→
  5→interface Paper {
  6→  id: string;
  7→  score: number;
  8→  corpusid: number;
  9→  title: string;
 10→  highlighted_title?: string | null;
 11→  year: number;
 12→  citationcount: number;
 13→  url: string;
 14→}
 15→
 16→interface SearchResult {
 17→  success: boolean;
 18→  query: string;
 19→  papers: Paper[];
 20→  options: {
 21→    use_time_decay: boolean;
 22→    use_boost: boolean;
 23→    use_boost_ranker?: boolean;
 24→    highlight_mode?: string;
 25→    limit: number;
 26→  };
 27→}
 28→
 29→type HighlightMode = 'none' | 'lexical';
 30→type SearchMode = 'semantic' | 'keyword' | 'hybrid';
 31→
 32→function highlightText(
 33→  text: string,
 34→  query: string,
 35→  mode: HighlightMode
 36→): React.ReactNode {
 37→  if (mode === 'none' || !query.trim()) {
 38→    return text;
 39→  }
 40→
 41→  const words = query.toLowerCase().split(/\s+/).filter(Boolean);
 42→
 43→  if (mode === 'lexical') {
 44→    const regex = new RegExp(`(${words.map(w => w.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')).join('|')})`, 'gi');
 45→    const parts = text.split(regex);
 46→
 47→    return parts.map((part, i) => {
 48→      const isMatch = words.some(w => part.toLowerCase() === w);
 49→      if (isMatch) {
 50→        return (
 51→          <span key={i} className="bg-yellow-200/80 px-0.5 rounded-sm">
 52→            {part}
 53→          </span>
 54→        );
 55→      }
 56→      return part;
 57→    });
 58→  }
 59→
 60→  return text;
 61→}
 62→
 63→function SearchIcon({ className }: { className?: string }) {
 64→  return (
 65→    <svg className={className} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
 66→      <path fillRule="evenodd" d="M9 3.5a5.5 5.5 0 100 11 5.5 5.5 0 000-11zM2 9a7 7 0 1112.452 4.391l3.328 3.329a.75.75 0 11-1.06 1.06l-3.329-3.328A7 7 0 012 9z" clipRule="evenodd" />
 67→    </svg>
 68→  );
 69→}
 70→
 71→function ChevronIcon({ open, className }: { open: boolean; className?: string }) {
 72→  return (
 73→    <svg
 74→      className={`${className} transition-transform duration-200 ${open ? 'rotate-180' : ''}`}
 75→      xmlns="http://www.w3.org/2000/svg"
 76→      viewBox="0 0 20 20"
 77→      fill="currentColor"
 78→    >
 79→      <path fillRule="evenodd" d="M5.22 8.22a.75.75 0 0 1 1.06 0L10 11.94l3.72-3.72a.75.75 0 1 1 1.06 1.06l-4.25 4.25a.75.75 0 0 1-1.06 0L5.22 9.28a.75.75 0 0 1 0-1.06Z" clipRule="evenodd" />
 80→    </svg>
 81→  );
 82→}
 83→
 84→function LoadingSpinner() {
 85→  return (
 86→    <div className="flex flex-col items-center justify-center py-16 gap-4">
 87→      <div className="relative">
 88→        <div className="w-10 h-10 border-3 border-blue-200 rounded-full" />
 89→        <div className="absolute top-0 left-0 w-10 h-10 border-3 border-transparent border-t-blue-600 rounded-full animate-spin-slow" />
 90→      </div>
 91→      <div className="flex gap-1.5">
 92→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" />
 93→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.2s' }} />
 94→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.4s' }} />
 95→      </div>
 96→      <p className="text-sm text-slate-400 font-medium">Searching papers...</p>
 97→    </div>
 98→  );
 99→}
100→
101→function formatCitations(count: number): string {
102→  if (count >= 1000) {
103→    return `${(count / 1000).toFixed(count >= 10000 ? 0 : 1)}k`;
104→  }
105→  return count.toLocaleString();
106→}
107→
108→export default function Home() {
109→  const [query, setQuery] = useState('');
110→  const [results, setResults] = useState<SearchResult | null>(null);
111→  const [loading, setLoading] = useState(false);
112→  const [error, setError] = useState<string | null>(null);
113→
114→  // Search mode
115→  const [searchMode, setSearchMode] = useState<SearchMode>('hybrid');
116→
117→  // Options
118→  const [useTimeDecay, setUseTimeDecay] = useState(false);
119→  const [useBoost, setUseBoost] = useState(false);
120→  const [useBoostRanker, setUseBoostRanker] = useState(false);
121→  const [highlightMode, setHighlightMode] = useState<HighlightMode>('lexical');
122→  const [limit, setLimit] = useState(10);
123→  const [showSettings, setShowSettings] = useState(false);
124→  const settingsRef = useRef<HTMLDivElement>(null);
125→
126→  // Autocomplete
127→  const [suggestions, setSuggestions] = useState<string[]>([]);
128→  const [showSuggestions, setShowSuggestions] = useState(false);
129→  const [selectedIdx, setSelectedIdx] = useState(-1);
130→  const debounceRef = useRef<ReturnType<typeof setTimeout> | null>(null);
131→  const inputRef = useRef<HTMLInputElement>(null);
132→
133→  useEffect(() => {
134→    if (debounceRef.current) clearTimeout(debounceRef.current);
135→    if (query.trim().length < 2) {
136→      setSuggestions([]);
137→      setShowSuggestions(false);
138→      return;
139→    }
140→    debounceRef.current = setTimeout(async () => {
141→      try {
142→        const res = await fetch('/api/autocomplete', {
143→          method: 'POST',
144→          headers: { 'Content-Type': 'application/json' },
145→          body: JSON.stringify({ query: query.trim(), limit: 5 }),
146→        });
147→        const data = await res.json();
148→        setSuggestions(data.titles || []);
149→        setShowSuggestions((data.titles || []).length > 0);
150→        setSelectedIdx(-1);
151→      } catch {
152→        setSuggestions([]);
153→      }
154→    }, 200);
155→    return () => { if (debounceRef.current) clearTimeout(debounceRef.current); };
156→  }, [query]);
157→
158→  // Close settings popover on click outside
159→  useEffect(() => {
160→    function handleClickOutside(e: MouseEvent) {
161→      if (settingsRef.current && !settingsRef.current.contains(e.target as Node)) {
162→        setShowSettings(false);
163→      }
164→    }
165→    if (showSettings) {
166→      document.addEventListener('mousedown', handleClickOutside);
167→      return () => document.removeEventListener('mousedown', handleClickOutside);
168→    }
169→  }, [showSettings]);
170→
171→  // Advanced time decay parameters
172→  const [showAdvanced, setShowAdvanced] = useState(false);
173→  const [origin, setOrigin] = useState(2025);
174→  const [offset, setOffset] = useState(5);
175→  const [decay, setDecay] = useState(0.8);
176→  const [scale, setScale] = useState(8);
177→
178→  const handleSearch = useCallback(async () => {
179→    if (!query.trim()) return;
180→
181→    setLoading(true);
182→    setError(null);
183→
184→    try {
185→      const response = await fetch('/api/search', {
186→        method: 'POST',
187→        headers: {
188→          'Content-Type': 'application/json',
189→        },
190→        body: JSON.stringify({
191→          query: query.trim(),
192→          limit,
193→          search_mode: searchMode,
194→          use_time_decay: useTimeDecay,
195→          use_boost: useBoost,
196→          use_boost_ranker: useBoostRanker,
197→          highlight_mode: highlightMode,
198→          time_decay_params: useTimeDecay ? { origin, offset, decay, scale } : undefined,
199→        }),
200→      });
201→
202→      if (!response.ok) {
203→        const errorData = await response.json();
204→        throw new Error(errorData.error || 'Search failed');
205→      }
206→
207→      const data = await response.json();
208→      setResults(data);
209→    } catch (err) {
210→      setError(err instanceof Error ? err.message : 'An error occurred');
211→    } finally {
212→      setLoading(false);
213→    }
214→  }, [query, limit, searchMode, useTimeDecay, useBoost, useBoostRanker, highlightMode, origin, offset, decay, scale]);
215→
216→  const handleKeyDown = (e: React.KeyboardEvent) => {
217→    if (showSuggestions && suggestions.length > 0) {
218→      if (e.key === 'ArrowDown') {
219→        e.preventDefault();
220→        setSelectedIdx(prev => (prev + 1) % suggestions.length);
221→        return;
222→      }
223→      if (e.key === 'ArrowUp') {
224→        e.preventDefault();
225→        setSelectedIdx(prev => (prev - 1 + suggestions.length) % suggestions.length);
226→        return;
227→      }
228→      if (e.key === 'Enter' && selectedIdx >= 0) {
229→        e.preventDefault();
230→        setQuery(suggestions[selectedIdx]);
231→        setShowSuggestions(false);
232→        return;
233→      }
234→      if (e.key === 'Escape') {
235→        setShowSuggestions(false);
236→        return;
237→      }
238→    }
239→    if (e.key === 'Enter') {
240→      setShowSuggestions(false);
241→      handleSearch();
242→    }
243→  };
244→
245→  const handleSearchModeChange = useCallback((mode: SearchMode) => {
246→    setSearchMode(mode);
247→  }, []);
248→
249→  // Re-execute search when searchMode changes (if a query exists)
250→  const prevSearchModeRef = useRef(searchMode);
251→  useEffect(() => {
252→    if (prevSearchModeRef.current !== searchMode && query.trim()) {
253→      prevSearchModeRef.current = searchMode;
254→      handleSearch();
255→    } else {
256→      prevSearchModeRef.current = searchMode;
257→    }
258→  }, [searchMode, query, handleSearch]);
259→
260→  // Build active settings summary
261→  const settingsSummary: string[] = [];
262→  settingsSummary.push(`${limit} results`);
263→  if (useBoostRanker) settingsSummary.push('Boost Ranker');
264→  if (useTimeDecay) settingsSummary.push('Time Decay');
265→  if (useBoost) settingsSummary.push('Citation Boost');
266→  if (highlightMode === 'lexical') settingsSummary.push('Keyword highlights');
267→
268→  return (
269→    <div className="min-h-screen bg-white">
270→      {/* Header */}
271→      <div className="bg-white border-b border-slate-200">
272→        <div className="max-w-6xl mx-auto px-4 pt-10 pb-8">
273→          <div className="text-center mb-8">
274→            <h1 className="text-3xl font-semibold tracking-tight text-slate-800 mb-1.5">
275→              Paper Search
276→            </h1>
277→            <p className="text-slate-500 text-sm">
278→              Semantic paper search powered by Milvus v2.6
279→            </p>
280→          </div>
281→
282→          {/* Search Mode Switcher + Settings Gear */}
283→          <div className="flex justify-center items-center gap-2 mb-6">
284→            <div className="inline-flex bg-slate-100 rounded-lg p-1 gap-1">
285→              {([
286→                { mode: 'semantic' as SearchMode, label: 'Semantic', subtitle: 'Conceptual similarity' },
287→                { mode: 'keyword' as SearchMode, label: 'Keyword', subtitle: 'Exact term matching' },
288→                { mode: 'hybrid' as SearchMode, label: 'Hybrid', subtitle: 'Combined search' },
289→              ]).map(({ mode, label, subtitle }) => (
290→                <button
291→                  key={mode}
292→                  onClick={() => handleSearchModeChange(mode)}
293→                  className={`px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
294→                    searchMode === mode
295→                      ? 'bg-blue-600 text-white shadow-sm'
296→                      : 'text-slate-600 hover:text-slate-800 hover:bg-slate-200'
297→                  }`}
298→                >
299→                  <span className="block">{label}</span>
300→                  <span className={`block text-[10px] font-normal mt-0.5 ${
301→                    searchMode === mode ? 'text-blue-100' : 'text-slate-400'
302→                  }`}>
303→                    {subtitle}
304→                  </span>
305→                </button>
306→              ))}
307→            </div>
308→
309→            {/* Settings Gear Button */}
310→            <div ref={settingsRef} className="relative">
311→              <button
312→                onClick={() => setShowSettings(!showSettings)}
313→                className={`p-2.5 rounded-lg border transition-colors duration-200 ${
314→                  showSettings
315→                    ? 'bg-slate-100 border-slate-300 text-slate-700'
316→                    : 'bg-white border-slate-200 text-slate-400 hover:text-slate-600 hover:border-slate-300'
317→                }`}
318→                title="Search settings"
319→              >
320→                <svg className="w-5 h-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
321→                  <path fillRule="evenodd" d="M7.84 1.804A1 1 0 018.82 1h2.36a1 1 0 01.98.804l.331 1.652a6.993 6.993 0 011.929 1.115l1.598-.54a1 1 0 011.186.447l1.18 2.044a1 1 0 01-.205 1.251l-1.267 1.113a7.047 7.047 0 010 2.228l1.267 1.113a1 1 0 01.206 1.25l-1.18 2.045a1 1 0 01-1.187.447l-1.598-.54a6.993 6.993 0 01-1.929 1.115l-.33 1.652a1 1 0 01-.98.804H8.82a1 1 0 01-.98-.804l-.331-1.652a6.993 6.993 0 01-1.929-1.115l-1.598.54a1 1 0 01-1.186-.447l-1.18-2.044a1 1 0 01.205-1.251l1.267-1.114a7.05 7.05 0 010-2.227L1.821 7.773a1 1 0 01-.206-1.25l1.18-2.045a1 1 0 011.187-.447l1.598.54A6.993 6.993 0 017.51 3.456l.33-1.652zM10 13a3 3 0 100-6 3 3 0 000 6z" clipRule="evenodd" />
322→                </svg>
323→              </button>
324→
325→              {/* Settings Popover */}
326→              {showSettings && (
327→                <div className="absolute right-0 top-full mt-2 w-72 bg-white rounded-lg border border-slate-200 shadow-lg z-30 overflow-hidden">
328→                  <div className="px-4 py-3 border-b border-slate-100">
329→                    <span className="text-sm font-semibold text-slate-700">Search Settings</span>
330→                  </div>
331→                  <div className="px-4 py-3 space-y-3">
332→                    {/* Ranker Toggles */}
333→                    <div>
334→                      <span className="text-xs font-medium text-slate-500 mb-2 block">Rankers</span>
335→                      <div className="space-y-1">
336→                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
337→                          <input
338→                            type="checkbox"
339→                            checked={useTimeDecay}
340→                            onChange={(e) => setUseTimeDecay(e.target.checked)}
341→                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
342→                          />
343→                          <div>
344→                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Time Decay</span>
345→                            <p className="text-xs text-slate-400 mt-0.5">Prefer recent papers</p>
346→                          </div>
347→                        </label>
348→
349→                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
350→                          <input
351→                            type="checkbox"
352→                            checked={useBoost}
353→                            onChange={(e) => setUseBoost(e.target.checked)}
354→                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
355→                          />
356→                          <div>
357→                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Citation Boost</span>
358→                            <p className="text-xs text-slate-400 mt-0.5">Boost highly-cited papers</p>
359→                          </div>
360→                        </label>
361→
362→                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
363→                          <input
364→                            type="checkbox"
365→                            checked={useBoostRanker}
366→                            onChange={(e) => setUseBoostRanker(e.target.checked)}
367→                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
368→                          />
369→                          <div>
370→                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Boost Ranker <span className="text-[10px] font-normal text-blue-600">v2.6</span></span>
371→                            <p className="text-xs text-slate-400 mt-0.5">Recency + citation boosting</p>
372→                          </div>
373→                        </label>
374→                      </div>
375→                    </div>
376→
377→                    {/* Highlighting */}
378→                    <div className="pt-2 border-t border-slate-100">
379→                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Highlighting</span>
380→                      <div className="flex bg-slate-100 rounded-lg p-0.5">
381→                        <button
382→                          onClick={() => setHighlightMode('none')}
383→                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
384→                            highlightMode === 'none'
385→                              ? 'bg-white text-slate-700 shadow-sm'
386→                              : 'text-slate-500 hover:text-slate-700'
387→                          }`}
388→                        >
389→                          Off
390→                        </button>
391→                        <button
392→                          onClick={() => setHighlightMode('lexical')}
393→                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
394→                            highlightMode === 'lexical'
395→                              ? 'bg-white text-slate-700 shadow-sm'
396→                              : 'text-slate-500 hover:text-slate-700'
397→                          }`}
398→                        >
399→                          Keyword Highlights
400→                        </button>
401→                      </div>
402→                    </div>
403→
404→                    {/* Result Limit */}
405→                    <div className="pt-2 border-t border-slate-100">
406→                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Results per search</span>
407→                      <select
408→                        value={limit}
409→                        onChange={(e) => setLimit(Number(e.target.value))}
410→                        className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
411→                      >
412→                        {[5, 10, 15, 20, 25, 50].map(n => (
413→                          <option key={n} value={n}>{n}</option>
414→                        ))}
415→                      </select>
416→                    </div>
417→
418→                    {/* Advanced Time Decay Settings */}
419→                    {useTimeDecay && (
420→                      <div className="pt-2 border-t border-slate-100">
421→                        <button
422→                          onClick={() => setShowAdvanced(!showAdvanced)}
423→                          className="text-xs text-blue-600 hover:text-blue-700 font-medium flex items-center gap-1"
424→                        >
425→                          <ChevronIcon open={showAdvanced} className="w-3 h-3" />
426→                          Advanced Settings
427→                        </button>
428→
429→                        {showAdvanced && (
430→                          <div className="grid grid-cols-2 gap-2 mt-2 bg-slate-50 p-3 rounded-lg">
431→                            <div>
432→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Origin</label>
433→                              <input
434→                                type="number"
435→                                value={origin}
436→                                onChange={(e) => setOrigin(Number(e.target.value))}
437→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
438→                              />
439→                            </div>
440→                            <div>
441→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Offset</label>
442→                              <input
443→                                type="number"
444→                                value={offset}
445→                                onChange={(e) => setOffset(Number(e.target.value))}
446→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
447→                              />
448→                            </div>
449→                            <div>
450→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Decay</label>
451→                              <input
452→                                type="number"
453→                                step="0.1"
454→                                min="0"
455→                                max="1"
456→                                value={decay}
457→                                onChange={(e) => setDecay(Number(e.target.value))}
458→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
459→                              />
460→                            </div>
461→                            <div>
462→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Scale</label>
463→                              <input
464→                                type="number"
465→                                value={scale}
466→                                onChange={(e) => setScale(Number(e.target.value))}
467→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
468→                              />
469→                            </div>
470→                          </div>
471→                        )}
472→                      </div>
473→                    )}
474→                  </div>
475→                </div>
476→              )}
477→            </div>
478→          </div>
479→
480→          {/* Search Box */}
481→          <div className="relative max-w-2xl mx-auto">
482→            <div className="relative">
483→              <SearchIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400 pointer-events-none" />
484→              <input
485→                ref={inputRef}
486→                type="text"
487→                value={query}
488→                onChange={(e) => setQuery(e.target.value)}
489→                onKeyDown={handleKeyDown}
490→                onFocus={() => suggestions.length > 0 && setShowSuggestions(true)}
491→                onBlur={() => setTimeout(() => setShowSuggestions(false), 150)}
492→                placeholder="Search for papers..."
493→                className="search-input w-full pl-12 pr-28 py-4 bg-white border border-slate-200 rounded-lg text-slate-700 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600 text-base shadow-sm"
494→                autoComplete="off"
495→              />
496→              <button
497→                onClick={handleSearch}
498→                disabled={loading || !query.trim()}
499→                className="absolute right-2 top-1/2 -translate-y-1/2 px-5 py-2.5 bg-blue-600 text-white rounded-md font-medium text-sm hover:bg-blue-700 disabled:bg-slate-200 disabled:text-slate-400 disabled:cursor-not-allowed"
500→              >
501→                Search
502→              </button>
503→            </div>
504→
505→            {/* Autocomplete dropdown */}
506→            {showSuggestions && suggestions.length > 0 && (
507→              <ul className="absolute z-20 left-0 right-0 mt-1.5 bg-white border border-slate-200 rounded-lg shadow-md overflow-hidden custom-scrollbar max-h-64 overflow-auto">
508→                {suggestions.map((title, i) => {
509→                  const q = query.trim();
510→                  const idx = title.indexOf(q);
511→                  return (
512→                    <li
513→                      key={i}
514→                      className={`px-4 py-2.5 text-sm cursor-pointer border-b border-slate-100 last:border-0 flex items-center gap-3 ${
515→                        i === selectedIdx
516→                          ? 'bg-blue-50 text-blue-900'
517→                          : 'text-slate-700 hover:bg-slate-50'
518→                      }`}
519→                      onMouseDown={() => {
520→                        setQuery(title);
521→                        setShowSuggestions(false);
522→                      }}
523→                    >
524→                      <SearchIcon className={`w-3.5 h-3.5 flex-shrink-0 ${i === selectedIdx ? 'text-blue-400' : 'text-slate-300'}`} />
525→                      <span className="truncate">
526→                        {idx >= 0 ? (
527→                          <>
528→                            {title.slice(0, idx)}
529→                            <strong className="font-semibold text-blue-600">{title.slice(idx, idx + q.length)}</strong>
530→                            {title.slice(idx + q.length)}
531→                          </>
532→                        ) : title}
533→                      </span>
534→                    </li>
535→                  );
536→                })}
537→              </ul>
538→            )}
539→          </div>
540→
541→          {/* Active settings summary */}
542→          <p className="text-center text-sm text-slate-500 mt-3">
543→            {settingsSummary.join(' · ')}
