                onClick={() => { if (query.trim()) handleSearch(); }}
                className="w-full px-3 py-2 bg-blue-600 text-white rounded-lg text-sm font-medium hover:bg-blue-700 disabled:bg-slate-200 disabled:text-slate-400 disabled:cursor-not-allowed"
                disabled={!query.trim()}
              >
                Apply Filters
              </button>
            </div>
          </aside>

          {/* Main results area */}
          <main className="flex-1 min-w-0">
            {/* Active Filter Chips */}
            {(yearMin || yearMax || citationMin) && (
              <div className="flex flex-wrap items-center gap-2 mb-4">
                {yearMin && (
                  <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                    Year &ge; {yearMin}
                    <button onClick={() => { setYearMin(''); if (query.trim()) handleSearch(); }} className="ml-0.5 text-blue-400 hover:text-blue-600">&times;</button>
                  </span>
                )}
                {yearMax && (
                  <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                    Year &le; {yearMax}
                    <button onClick={() => { setYearMax(''); if (query.trim()) handleSearch(); }} className="ml-0.5 text-blue-400 hover:text-blue-600">&times;</button>
                  </span>
                )}
                {citationMin && (
                  <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                    Citations &ge; {citationMin}
                    <button onClick={() => { setCitationMin(''); if (query.trim()) handleSearch(); }} className="ml-0.5 text-blue-400 hover:text-blue-600">&times;</button>
                  </span>
                )}
                <button
                  onClick={() => { setYearMin(''); setYearMax(''); setCitationMin(''); if (query.trim()) handleSearch(); }}
                  className="text-xs text-slate-500 hover:text-slate-700 underline ml-1"
                >
                  Clear all
                </button>
              </div>
            )}

            {/* Error Display */}
