 20→}
 21→
 22→@theme inline {
 23→  --color-background: var(--background);
 24→  --color-foreground: var(--foreground);
 25→  --font-sans: var(--font-geist-sans);
 26→  --font-mono: var(--font-geist-mono);
 27→}
 28→
 29→body {
 30→  background: var(--background);
 31→  color: var(--foreground);
 32→  font-family: var(--font-geist-sans), system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
 33→  -webkit-font-smoothing: antialiased;
 34→  -moz-osx-font-smoothing: grayscale;
 35→}
 36→
 37→/* Typography hierarchy */
 38→h1, h2, h3 {
 39→  font-weight: 600;
 40→  color: #334155;
 41→}
 42→
 43→label, .label {
 44→  font-weight: 500;
 45→}
 46→
 47→/* Custom focus styles */
 48→input:focus,
 49→select:focus,
 50→button:focus {
 51→  outline: none;
 52→}
 53→
 54→/* Search input focus ring animation */
 55→.search-input {
 56→  transition: border-color 200ms ease, box-shadow 200ms ease;
 57→}
 58→
 59→.search-input:focus {
 60→  border-color: #2563eb;
 61→  box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.15);
 62→}
