  1→'use client';
  2→
  3→import { useState, useCallback, useEffect, useRef } from 'react';
  4→
  5→interface Paper {
  6→  id: string;
  7→  score: number;
  8→  corpusid: number;
  9→  title: string;
 10→  highlighted_title?: string | null;
 11→  year: number;
 12→  citationcount: number;
 13→  url: string;
 14→}
 15→
 16→interface SearchResult {
 17→  success: boolean;
 18→  query: string;
 19→  papers: Paper[];
 20→  options: {
 21→    use_time_decay: boolean;
 22→    use_boost: boolean;
 23→    use_boost_ranker?: boolean;
 24→    highlight_mode?: string;
 25→    limit: number;
 26→  };
 27→}
 28→
 29→type HighlightMode = 'none' | 'lexical';
 30→type SearchMode = 'semantic' | 'keyword' | 'hybrid';
 31→
 32→function highlightText(
 33→  text: string,
 34→  query: string,
 35→  mode: HighlightMode
 36→): React.ReactNode {
 37→  if (mode === 'none' || !query.trim()) {
 38→    return text;
 39→  }
 40→
 41→  const words = query.toLowerCase().split(/\s+/).filter(Boolean);
 42→
 43→  if (mode === 'lexical') {
 44→    const regex = new RegExp(`(${words.map(w => w.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')).join('|')})`, 'gi');
 45→    const parts = text.split(regex);
 46→
 47→    return parts.map((part, i) => {
 48→      const isMatch = words.some(w => part.toLowerCase() === w);
 49→      if (isMatch) {
 50→        return (
 51→          <span key={i} className="bg-yellow-200/80 px-0.5 rounded-sm">
 52→            {part}
 53→          </span>
 54→        );
 55→      }
 56→      return part;
 57→    });
 58→  }
 59→
 60→  return text;
 61→}
 62→
 63→function SearchIcon({ className }: { className?: string }) {
 64→  return (
 65→    <svg className={className} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
 66→      <path fillRule="evenodd" d="M9 3.5a5.5 5.5 0 100 11 5.5 5.5 0 000-11zM2 9a7 7 0 1112.452 4.391l3.328 3.329a.75.75 0 11-1.06 1.06l-3.329-3.328A7 7 0 012 9z" clipRule="evenodd" />
 67→    </svg>
 68→  );
 69→}
 70→
 71→function ChevronIcon({ open, className }: { open: boolean; className?: string }) {
 72→  return (
 73→    <svg
 74→      className={`${className} transition-transform duration-200 ${open ? 'rotate-180' : ''}`}
 75→      xmlns="http://www.w3.org/2000/svg"
 76→      viewBox="0 0 20 20"
 77→      fill="currentColor"
 78→    >
 79→      <path fillRule="evenodd" d="M5.22 8.22a.75.75 0 0 1 1.06 0L10 11.94l3.72-3.72a.75.75 0 1 1 1.06 1.06l-4.25 4.25a.75.75 0 0 1-1.06 0L5.22 9.28a.75.75 0 0 1 0-1.06Z" clipRule="evenodd" />
 80→    </svg>
 81→  );
 82→}
 83→
 84→function LoadingSpinner() {
 85→  return (
 86→    <div className="flex flex-col items-center justify-center py-16 gap-4">
 87→      <div className="relative">
 88→        <div className="w-10 h-10 border-3 border-blue-200 rounded-full" />
 89→        <div className="absolute top-0 left-0 w-10 h-10 border-3 border-transparent border-t-blue-600 rounded-full animate-spin-slow" />
 90→      </div>
 91→      <div className="flex gap-1.5">
 92→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" />
 93→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.2s' }} />
 94→        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.4s' }} />
 95→      </div>
 96→      <p className="text-sm text-slate-400 font-medium">Searching papers...</p>
 97→    </div>
 98→  );
 99→}
100→
101→function formatCitations(count: number): string {
102→  if (count >= 1000) {
103→    return `${(count / 1000).toFixed(count >= 10000 ? 0 : 1)}k`;
104→  }
105→  return count.toLocaleString();
106→}
107→
108→export default function Home() {
109→  const [query, setQuery] = useState('');
110→  const [results, setResults] = useState<SearchResult | null>(null);
111→  const [loading, setLoading] = useState(false);
112→  const [error, setError] = useState<string | null>(null);
113→
114→  // Search mode
115→  const [searchMode, setSearchMode] = useState<SearchMode>('hybrid');
116→
117→  // Options
118→  const [useTimeDecay, setUseTimeDecay] = useState(false);
119→  const [useBoost, setUseBoost] = useState(false);
120→  const [useBoostRanker, setUseBoostRanker] = useState(false);
121→  const [highlightMode, setHighlightMode] = useState<HighlightMode>('lexical');
122→  const [limit, setLimit] = useState(10);
123→  const [showSettings, setShowSettings] = useState(false);
124→  const settingsRef = useRef<HTMLDivElement>(null);
125→
126→  // Filters
127→  const [yearMin, setYearMin] = useState<string>('');
128→  const [yearMax, setYearMax] = useState<string>('');
129→  const [citationMin, setCitationMin] = useState<string>('');
130→
131→  // Suggested query trigger
132→  const pendingSuggestedSearch = useRef(false);
133→
134→  // Autocomplete
135→  const [suggestions, setSuggestions] = useState<string[]>([]);
136→  const [showSuggestions, setShowSuggestions] = useState(false);
137→  const [selectedIdx, setSelectedIdx] = useState(-1);
138→  const debounceRef = useRef<ReturnType<typeof setTimeout> | null>(null);
139→  const inputRef = useRef<HTMLInputElement>(null);
140→
141→  useEffect(() => {
142→    if (debounceRef.current) clearTimeout(debounceRef.current);
143→    if (query.trim().length < 2) {
144→      setSuggestions([]);
145→      setShowSuggestions(false);
146→      return;
147→    }
148→    debounceRef.current = setTimeout(async () => {
149→      try {
150→        const res = await fetch('/api/autocomplete', {
151→          method: 'POST',
152→          headers: { 'Content-Type': 'application/json' },
153→          body: JSON.stringify({ query: query.trim(), limit: 5 }),
154→        });
155→        const data = await res.json();
156→        setSuggestions(data.titles || []);
157→        setShowSuggestions((data.titles || []).length > 0);
158→        setSelectedIdx(-1);
159→      } catch {
160→        setSuggestions([]);
161→      }
162→    }, 200);
163→    return () => { if (debounceRef.current) clearTimeout(debounceRef.current); };
164→  }, [query]);
165→
166→  // Close settings popover on click outside
167→  useEffect(() => {
168→    function handleClickOutside(e: MouseEvent) {
169→      if (settingsRef.current && !settingsRef.current.contains(e.target as Node)) {
170→        setShowSettings(false);
171→      }
172→    }
173→    if (showSettings) {
174→      document.addEventListener('mousedown', handleClickOutside);
175→      return () => document.removeEventListener('mousedown', handleClickOutside);
176→    }
177→  }, [showSettings]);
178→
179→  // Advanced time decay parameters
180→  const [showAdvanced, setShowAdvanced] = useState(false);
181→  const [origin, setOrigin] = useState(2025);
182→  const [offset, setOffset] = useState(5);
183→  const [decay, setDecay] = useState(0.8);
184→  const [scale, setScale] = useState(8);
185→
186→  // Build Milvus filter expression from filter state
187→  const buildFilterExpr = useCallback(() => {
188→    const parts: string[] = [];
189→    if (yearMin) parts.push(`year >= ${yearMin}`);
190→    if (yearMax) parts.push(`year <= ${yearMax}`);
191→    if (citationMin) parts.push(`citationcount >= ${citationMin}`);
192→    return parts.join(' and ');
193→  }, [yearMin, yearMax, citationMin]);
194→
195→  const handleSearch = useCallback(async () => {
196→    if (!query.trim()) return;
197→
198→    setLoading(true);
199→    setError(null);
200→
201→    try {
202→      const filterExpr = buildFilterExpr();
203→      const response = await fetch('/api/search', {
204→        method: 'POST',
205→        headers: {
206→          'Content-Type': 'application/json',
207→        },
208→        body: JSON.stringify({
209→          query: query.trim(),
210→          limit,
211→          search_mode: searchMode,
212→          use_time_decay: useTimeDecay,
213→          use_boost: useBoost,
214→          use_boost_ranker: useBoostRanker,
215→          highlight_mode: highlightMode,
216→          filter: filterExpr || undefined,
217→          time_decay_params: useTimeDecay ? { origin, offset, decay, scale } : undefined,
218→        }),
219→      });
220→
221→      if (!response.ok) {
222→        const errorData = await response.json();
223→        throw new Error(errorData.error || 'Search failed');
224→      }
225→
226→      const data = await response.json();
227→      setResults(data);
228→    } catch (err) {
229→      setError(err instanceof Error ? err.message : 'An error occurred');
230→    } finally {
231→      setLoading(false);
232→    }
233→  }, [query, limit, searchMode, useTimeDecay, useBoost, useBoostRanker, highlightMode, origin, offset, decay, scale, buildFilterExpr]);
234→
235→  // Trigger search after a suggested query is clicked (query state is updated)
236→  useEffect(() => {
237→    if (pendingSuggestedSearch.current && query.trim()) {
238→      pendingSuggestedSearch.current = false;
239→      handleSearch();
240→    }
241→  }, [query, handleSearch]);
242→
243→  const handleKeyDown = (e: React.KeyboardEvent) => {
244→    if (showSuggestions && suggestions.length > 0) {
245→      if (e.key === 'ArrowDown') {
246→        e.preventDefault();
247→        setSelectedIdx(prev => (prev + 1) % suggestions.length);
248→        return;
249→      }
250→      if (e.key === 'ArrowUp') {
251→        e.preventDefault();
252→        setSelectedIdx(prev => (prev - 1 + suggestions.length) % suggestions.length);
253→        return;
254→      }
255→      if (e.key === 'Enter' && selectedIdx >= 0) {
256→        e.preventDefault();
257→        setQuery(suggestions[selectedIdx]);
258→        setShowSuggestions(false);
259→        return;
260→      }
261→      if (e.key === 'Escape') {
262→        setShowSuggestions(false);
263→        return;
264→      }
265→    }
266→    if (e.key === 'Enter') {
267→      setShowSuggestions(false);
268→      handleSearch();
269→    }
270→  };
271→
272→  const handleSearchModeChange = useCallback((mode: SearchMode) => {
273→    setSearchMode(mode);
274→  }, []);
275→
276→  // Re-execute search when searchMode changes (if a query exists)
277→  const prevSearchModeRef = useRef(searchMode);
278→  useEffect(() => {
279→    if (prevSearchModeRef.current !== searchMode && query.trim()) {
280→      prevSearchModeRef.current = searchMode;
281→      handleSearch();
282→    } else {
283→      prevSearchModeRef.current = searchMode;
284→    }
285→  }, [searchMode, query, handleSearch]);
286→
287→  // Re-execute search when filters change via chip removal (if a query exists and results are showing)
288→  const filterExpr = buildFilterExpr();
289→  const prevFilterRef = useRef(filterExpr);
290→  const hasResultsRef = useRef(false);
291→  hasResultsRef.current = results !== null;
292→  useEffect(() => {
293→    if (prevFilterRef.current !== filterExpr && query.trim() && hasResultsRef.current) {
294→      prevFilterRef.current = filterExpr;
295→      handleSearch();
296→    } else {
297→      prevFilterRef.current = filterExpr;
298→    }
299→  }, [filterExpr, query, handleSearch]);
300→
301→  // Build active settings summary
302→  const settingsSummary: string[] = [];
303→  settingsSummary.push(`${limit} results`);
304→  if (useBoostRanker) settingsSummary.push('Boost Ranker');
305→  if (useTimeDecay) settingsSummary.push('Time Decay');
306→  if (useBoost) settingsSummary.push('Citation Boost');
307→  if (highlightMode === 'lexical') settingsSummary.push('Keyword highlights');
308→
309→  return (
310→    <div className="min-h-screen bg-white">
311→      {/* Header */}
312→      <div className="bg-white border-b border-slate-200">
313→        <div className="max-w-6xl mx-auto px-4 pt-10 pb-8">
314→          <div className="text-center mb-8">
315→            <h1 className="text-3xl font-semibold tracking-tight text-slate-800 mb-1.5">
316→              Paper Search
317→            </h1>
318→            <p className="text-slate-500 text-sm">
319→              Semantic paper search powered by Milvus v2.6
320→            </p>
321→          </div>
322→
323→          {/* Search Mode Switcher + Settings Gear */}
324→          <div className="flex justify-center items-center gap-2 mb-6">
325→            <div className="inline-flex bg-slate-100 rounded-lg p-1 gap-1">
326→              {([
327→                { mode: 'semantic' as SearchMode, label: 'Semantic', subtitle: 'Conceptual similarity' },
328→                { mode: 'keyword' as SearchMode, label: 'Keyword', subtitle: 'Exact term matching' },
329→                { mode: 'hybrid' as SearchMode, label: 'Hybrid', subtitle: 'Combined search' },
330→              ]).map(({ mode, label, subtitle }) => (
331→                <button
332→                  key={mode}
333→                  onClick={() => handleSearchModeChange(mode)}
334→                  className={`px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
335→                    searchMode === mode
336→                      ? 'bg-blue-600 text-white shadow-sm'
337→                      : 'text-slate-600 hover:text-slate-800 hover:bg-slate-200'
338→                  }`}
339→                >
340→                  <span className="block">{label}</span>
341→                  <span className={`block text-[10px] font-normal mt-0.5 ${
342→                    searchMode === mode ? 'text-blue-100' : 'text-slate-400'
343→                  }`}>
344→                    {subtitle}
345→                  </span>
346→                </button>
347→              ))}
348→            </div>
349→
350→            {/* Settings Gear Button */}
351→            <div ref={settingsRef} className="relative">
352→              <button
353→                onClick={() => setShowSettings(!showSettings)}
354→                className={`p-2.5 rounded-lg border transition-colors duration-200 ${
355→                  showSettings
356→                    ? 'bg-slate-100 border-slate-300 text-slate-700'
357→                    : 'bg-white border-slate-200 text-slate-400 hover:text-slate-600 hover:border-slate-300'
358→                }`}
359→                title="Search settings"
360→              >
361→                <svg className="w-5 h-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
362→                  <path fillRule="evenodd" d="M7.84 1.804A1 1 0 018.82 1h2.36a1 1 0 01.98.804l.331 1.652a6.993 6.993 0 011.929 1.115l1.598-.54a1 1 0 011.186.447l1.18 2.044a1 1 0 01-.205 1.251l-1.267 1.113a7.047 7.047 0 010 2.228l1.267 1.113a1 1 0 01.206 1.25l-1.18 2.045a1 1 0 01-1.187.447l-1.598-.54a6.993 6.993 0 01-1.929 1.115l-.33 1.652a1 1 0 01-.98.804H8.82a1 1 0 01-.98-.804l-.331-1.652a6.993 6.993 0 01-1.929-1.115l-1.598.54a1 1 0 01-1.186-.447l-1.18-2.044a1 1 0 01.205-1.251l1.267-1.114a7.05 7.05 0 010-2.227L1.821 7.773a1 1 0 01-.206-1.25l1.18-2.045a1 1 0 011.187-.447l1.598.54A6.993 6.993 0 017.51 3.456l.33-1.652zM10 13a3 3 0 100-6 3 3 0 000 6z" clipRule="evenodd" />
363→                </svg>
364→              </button>
365→
366→              {/* Settings Popover */}
367→              {showSettings && (
368→                <div className="absolute right-0 top-full mt-2 w-72 bg-white rounded-lg border border-slate-200 shadow-lg z-30 overflow-hidden">
369→                  <div className="px-4 py-3 border-b border-slate-100">
370→                    <span className="text-sm font-semibold text-slate-700">Search Settings</span>
371→                  </div>
372→                  <div className="px-4 py-3 space-y-3">
373→                    {/* Ranker Toggles */}
374→                    <div>
375→                      <span className="text-xs font-medium text-slate-500 mb-2 block">Rankers</span>
376→                      <div className="space-y-1">
377→                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
378→                          <input
379→                            type="checkbox"
380→                            checked={useTimeDecay}
381→                            onChange={(e) => setUseTimeDecay(e.target.checked)}
382→                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
383→                          />
384→                          <div>
385→                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Time Decay</span>
386→                            <p className="text-xs text-slate-400 mt-0.5">Prefer recent papers</p>
387→                          </div>
388→                        </label>
389→
390→                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
391→                          <input
392→                            type="checkbox"
393→                            checked={useBoost}
394→                            onChange={(e) => setUseBoost(e.target.checked)}
395→                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
396→                          />
397→                          <div>
398→                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Citation Boost</span>
399→                            <p className="text-xs text-slate-400 mt-0.5">Boost highly-cited papers</p>
400→                          </div>
401→                        </label>
402→
403→                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
404→                          <input
405→                            type="checkbox"
406→                            checked={useBoostRanker}
407→                            onChange={(e) => setUseBoostRanker(e.target.checked)}
408→                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
409→                          />
410→                          <div>
411→                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Boost Ranker <span className="text-[10px] font-normal text-blue-600">v2.6</span></span>
412→                            <p className="text-xs text-slate-400 mt-0.5">Recency + citation boosting</p>
413→                          </div>
414→                        </label>
415→                      </div>
416→                    </div>
417→
418→                    {/* Highlighting */}
419→                    <div className="pt-2 border-t border-slate-100">
420→                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Highlighting</span>
421→                      <div className="flex bg-slate-100 rounded-lg p-0.5">
422→                        <button
423→                          onClick={() => setHighlightMode('none')}
424→                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
425→                            highlightMode === 'none'
426→                              ? 'bg-white text-slate-700 shadow-sm'
427→                              : 'text-slate-500 hover:text-slate-700'
428→                          }`}
429→                        >
430→                          Off
431→                        </button>
432→                        <button
433→                          onClick={() => setHighlightMode('lexical')}
434→                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
435→                            highlightMode === 'lexical'
436→                              ? 'bg-white text-slate-700 shadow-sm'
437→                              : 'text-slate-500 hover:text-slate-700'
438→                          }`}
439→                        >
440→                          Keyword Highlights
441→                        </button>
442→                      </div>
443→                    </div>
444→
445→                    {/* Result Limit */}
446→                    <div className="pt-2 border-t border-slate-100">
447→                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Results per search</span>
448→                      <select
449→                        value={limit}
450→                        onChange={(e) => setLimit(Number(e.target.value))}
451→                        className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
452→                      >
453→                        {[5, 10, 15, 20, 25, 50].map(n => (
454→                          <option key={n} value={n}>{n}</option>
455→                        ))}
456→                      </select>
457→                    </div>
458→
459→                    {/* Advanced Time Decay Settings */}
460→                    {useTimeDecay && (
461→                      <div className="pt-2 border-t border-slate-100">
462→                        <button
463→                          onClick={() => setShowAdvanced(!showAdvanced)}
464→                          className="text-xs text-blue-600 hover:text-blue-700 font-medium flex items-center gap-1"
465→                        >
466→                          <ChevronIcon open={showAdvanced} className="w-3 h-3" />
467→                          Advanced Settings
468→                        </button>
469→
470→                        {showAdvanced && (
471→                          <div className="grid grid-cols-2 gap-2 mt-2 bg-slate-50 p-3 rounded-lg">
472→                            <div>
473→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Origin</label>
474→                              <input
475→                                type="number"
476→                                value={origin}
477→                                onChange={(e) => setOrigin(Number(e.target.value))}
478→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
479→                              />
480→                            </div>
481→                            <div>
482→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Offset</label>
483→                              <input
484→                                type="number"
485→                                value={offset}
486→                                onChange={(e) => setOffset(Number(e.target.value))}
487→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
488→                              />
489→                            </div>
490→                            <div>
491→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Decay</label>
492→                              <input
493→                                type="number"
494→                                step="0.1"
495→                                min="0"
496→                                max="1"
497→                                value={decay}
498→                                onChange={(e) => setDecay(Number(e.target.value))}
499→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
500→                              />
501→                            </div>
502→                            <div>
503→                              <label className="block text-xs text-slate-500 mb-1 font-medium">Scale</label>
504→                              <input
505→                                type="number"
506→                                value={scale}
507→                                onChange={(e) => setScale(Number(e.target.value))}
508→                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
509→                              />
510→                            </div>
511→                          </div>
512→                        )}
513→                      </div>
514→                    )}
515→                  </div>
516→                </div>
517→              )}
518→            </div>
519→          </div>
520→
521→          {/* Search Box */}
522→          <div className="relative max-w-2xl mx-auto">
523→            <div className="relative">
524→              <SearchIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400 pointer-events-none" />
525→              <input
526→                ref={inputRef}
527→                type="text"
528→                value={query}
529→                onChange={(e) => setQuery(e.target.value)}
530→                onKeyDown={handleKeyDown}
531→                onFocus={() => suggestions.length > 0 && setShowSuggestions(true)}
532→                onBlur={() => setTimeout(() => setShowSuggestions(false), 150)}
533→                placeholder="Search for papers..."
534→                className="search-input w-full pl-12 pr-28 py-4 bg-white border border-slate-200 rounded-lg text-slate-700 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600 text-base shadow-sm"
535→                autoComplete="off"
536→              />
537→              <button
538→                onClick={handleSearch}
539→                disabled={loading || !query.trim()}
540→                className="absolute right-2 top-1/2 -translate-y-1/2 px-5 py-2.5 bg-blue-600 text-white rounded-md font-medium text-sm hover:bg-blue-700 disabled:bg-slate-200 disabled:text-slate-400 disabled:cursor-not-allowed"
541→              >
542→                Search
543→              </button>
544→            </div>
545→
546→            {/* Autocomplete dropdown */}
547→            {showSuggestions && suggestions.length > 0 && (
548→              <ul className="absolute z-20 left-0 right-0 mt-1.5 bg-white border border-slate-200 rounded-lg shadow-md overflow-hidden custom-scrollbar max-h-64 overflow-auto">
549→                {suggestions.map((title, i) => {
550→                  const q = query.trim();
551→                  const idx = title.indexOf(q);
552→                  return (
553→                    <li
554→                      key={i}
555→                      className={`px-4 py-2.5 text-sm cursor-pointer border-b border-slate-100 last:border-0 flex items-center gap-3 ${
556→                        i === selectedIdx
557→                          ? 'bg-blue-50 text-blue-900'
558→                          : 'text-slate-700 hover:bg-slate-50'
559→                      }`}
560→                      onMouseDown={() => {
561→                        setQuery(title);
562→                        setShowSuggestions(false);
563→                      }}
564→                    >
565→                      <SearchIcon className={`w-3.5 h-3.5 flex-shrink-0 ${i === selectedIdx ? 'text-blue-400' : 'text-slate-300'}`} />
566→                      <span className="truncate">
567→                        {idx >= 0 ? (
568→                          <>
569→                            {title.slice(0, idx)}
570→                            <strong className="font-semibold text-blue-600">{title.slice(idx, idx + q.length)}</strong>
571→                            {title.slice(idx + q.length)}
572→                          </>
573→                        ) : title}
574→                      </span>
575→                    </li>
576→                  );
577→                })}
578→              </ul>
579→            )}
580→          </div>
581→
582→          {/* Active settings summary */}
583→          <p className="text-center text-sm text-slate-500 mt-3">
584→            {settingsSummary.join(' · ')}
585→          </p>
586→        </div>
587→      </div>
588→
589→      {/* Main content */}
590→      <div className="max-w-6xl mx-auto px-4 py-6">
591→        <div className="flex flex-col md:flex-row gap-6">
592→          {/* Filter Sidebar */}
593→          <aside className="w-full md:w-64 flex-shrink-0">
594→            <div className="bg-gray-50 rounded-lg p-4 space-y-5">
595→              <h3 className="text-sm font-semibold text-slate-700">Filters</h3>
596→
597→              {/* Year Range */}
598→              <div>
599→                <label className="text-xs font-semibold text-slate-600 mb-2 block">Year Range</label>
600→                <div className="flex items-center gap-2">
601→                  <input
602→                    type="number"
603→                    placeholder="Min"
604→                    value={yearMin}
605→                    onChange={(e) => setYearMin(e.target.value)}
606→                    className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
607→                  />
608→                  <span className="text-slate-400 text-xs">to</span>
609→                  <input
610→                    type="number"
611→                    placeholder="Max"
612→                    value={yearMax}
613→                    onChange={(e) => setYearMax(e.target.value)}
614→                    className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
615→                  />
616→                </div>
617→              </div>
618→
619→              {/* Citation Count */}
620→              <div>
621→                <label className="text-xs font-semibold text-slate-600 mb-2 block">Citation Count</label>
622→                <div className="space-y-1">
623→                  {[
624→                    { label: 'Any', value: '' },
625→                    { label: '10+', value: '10' },
626→                    { label: '100+', value: '100' },
627→                    { label: '500+', value: '500' },
628→                    { label: '1000+', value: '1000' },
629→                  ].map(({ label, value }) => (
