                    <button
                      key={suggestion}
                      onClick={() => {
                        pendingSuggestedSearch.current = true;
                        setQuery(suggestion);
                        setShowSuggestions(false);
                      }}
                      className="px-3.5 py-1.5 text-sm text-blue-600 bg-blue-50 border border-blue-200 rounded-full hover:bg-blue-100 transition-colors duration-200"
                    >
                      {suggestion}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </main>
        </div>

        {/* Footer */}
        <footer className="py-8 mt-8 border-t border-slate-200 text-center">
          <p className="text-xs text-slate-400">
            Powered by <span className="font-medium text-slate-500">Milvus</span> vector database
            &nbsp;&middot;&nbsp;
            <span className="font-medium text-slate-500">OpenAI</span> text-embedding-3-small
          </p>
        </footer>
      </div>
    </div>
  );
}

