                                <svg className="w-3.5 h-3.5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                                  <path d="M3.505 2.365A41.369 41.369 0 019 2c1.863 0 3.697.124 5.495.365 1.247.167 2.18 1.108 2.435 2.268a4.45 4.45 0 00-.577-.069 43.141 43.141 0 00-4.706 0C9.229 4.696 7.5 6.727 7.5 8.998v2.24c0 1.413.67 2.735 1.76 3.562l-2.98 2.98A.75.75 0 015 17.25v-3.443c-.501-.048-1-.106-1.495-.172C2.033 13.438 1 12.162 1 10.72V5.28c0-1.441 1.033-2.717 2.505-2.914z" />
                                  <path d="M14 6c.762 0 1.52.02 2.272.062 1.057.062 1.978.93 1.978 2.11v5.064c0 1.18-.921 2.048-1.978 2.11a40 40 0 01-1.554.065c.014.158.014.318 0 .476l-.004.048c-.017.219-.04.442-.07.666-.114.867-.358 1.783-.752 2.56a.75.75 0 01-1.329-.068c-.344-.753-.558-1.503-.673-2.172a28.4 28.4 0 01-.192-1.335l-.004-.04c-.638-.198-1.22-.56-1.693-1.05V8.998c0-1.566 1.164-2.913 2.772-3.035A41.2 41.2 0 0114 6z" />
                                </svg>
                                {formatCitations(paper.citationcount)}
                              </span>
                            </div>
                          </div>
                          <span className="inline-flex items-center text-xs font-medium bg-slate-100 text-slate-600 px-2.5 py-1 rounded-full tabular-nums whitespace-nowrap mt-0.5">
                            {Math.round(paper.score * 100)}%
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* Empty state - no search yet */}
            {!loading && !results && !error && (
              <div className="text-center py-20">
                <div className="w-16 h-16 mx-auto mb-5 rounded-2xl bg-blue-50 flex items-center justify-center">
                  <SearchIcon className="w-7 h-7 text-slate-300" />
                </div>
                <h2 className="text-lg font-semibold text-slate-400 mb-1">Search academic papers</h2>
                <p className="text-sm text-slate-400 mb-6">
                  Try one of these queries:
                </p>
                <div className="flex flex-wrap justify-center gap-2">
                  {[
                    'transformer attention mechanisms',
                    'neural network pruning',
                    'large language models',
                    'graph neural networks',
                    'reinforcement learning robotics',
                  ].map((suggestion) => (
                    <button
                      key={suggestion}
                      onClick={() => {
                        pendingSuggestedSearch.current = true;
                        setQuery(suggestion);
                        setShowSuggestions(false);
                      }}
                      className="px-3.5 py-1.5 text-sm text-blue-600 bg-blue-50 border border-blue-200 rounded-full hover:bg-blue-100 transition-colors duration-200"
                    >
                      {suggestion}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </main>
        </div>

        {/* Footer */}
        <footer className="py-8 mt-8 border-t border-slate-200 text-center">
          <p className="text-xs text-slate-400">
            Powered by <span className="font-medium text-slate-500">Milvus</span> vector database
            &nbsp;&middot;&nbsp;
            <span className="font-medium text-slate-500">OpenAI</span> text-embedding-3-small
          </p>
        </footer>
      </div>
    </div>
  );
}

'use client';

import { useState, useCallback, useEffect, useRef } from 'react';

interface Paper {
  id: string;
  score: number;
  corpusid: number;
  title: string;
  highlighted_title?: string | null;
  year: number;
  citationcount: number;
  url: string;
}

interface SearchResult {
  success: boolean;
  query: string;
  papers: Paper[];
  options: {
    use_time_decay: boolean;
    use_boost: boolean;
    use_boost_ranker?: boolean;
    highlight_mode?: string;
    limit: number;
  };
}

type HighlightMode = 'none' | 'lexical';
type SearchMode = 'semantic' | 'keyword' | 'hybrid';

function highlightText(
  text: string,
  query: string,
  mode: HighlightMode
): React.ReactNode {
  if (mode === 'none' || !query.trim()) {
    return text;
  }

  const words = query.toLowerCase().split(/\s+/).filter(Boolean);

  if (mode === 'lexical') {
    const regex = new RegExp(`(${words.map(w => w.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')).join('|')})`, 'gi');
    const parts = text.split(regex);

    return parts.map((part, i) => {
      const isMatch = words.some(w => part.toLowerCase() === w);
      if (isMatch) {
        return (
          <span key={i} className="bg-yellow-200/80 px-0.5 rounded-sm">
            {part}
          </span>
        );
      }
      return part;
    });
  }

  return text;
}

function SearchIcon({ className }: { className?: string }) {
  return (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
      <path fillRule="evenodd" d="M9 3.5a5.5 5.5 0 100 11 5.5 5.5 0 000-11zM2 9a7 7 0 1112.452 4.391l3.328 3.329a.75.75 0 11-1.06 1.06l-3.329-3.328A7 7 0 012 9z" clipRule="evenodd" />
    </svg>
  );
}

function ChevronIcon({ open, className }: { open: boolean; className?: string }) {
  return (
    <svg
      className={`${className} transition-transform duration-200 ${open ? 'rotate-180' : ''}`}
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 20 20"
      fill="currentColor"
    >
      <path fillRule="evenodd" d="M5.22 8.22a.75.75 0 0 1 1.06 0L10 11.94l3.72-3.72a.75.75 0 1 1 1.06 1.06l-4.25 4.25a.75.75 0 0 1-1.06 0L5.22 9.28a.75.75 0 0 1 0-1.06Z" clipRule="evenodd" />
    </svg>
  );
}

function LoadingSpinner() {
  return (
    <div className="flex flex-col items-center justify-center py-16 gap-4">
      <div className="relative">
        <div className="w-10 h-10 border-3 border-blue-200 rounded-full" />
        <div className="absolute top-0 left-0 w-10 h-10 border-3 border-transparent border-t-blue-600 rounded-full animate-spin-slow" />
      </div>
      <div className="flex gap-1.5">
        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" />
        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.2s' }} />
        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.4s' }} />
      </div>
      <p className="text-sm text-slate-400 font-medium">Searching papers...</p>
    </div>
  );
}

function formatCitations(count: number): string {
  if (count >= 1000) {
    return `${(count / 1000).toFixed(count >= 10000 ? 0 : 1)}k`;
  }
  return count.toLocaleString();
}

export default function Home() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<SearchResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Search mode
  const [searchMode, setSearchMode] = useState<SearchMode>('hybrid');

  // Options
  const [useTimeDecay, setUseTimeDecay] = useState(false);
  const [useBoost, setUseBoost] = useState(false);
  const [useBoostRanker, setUseBoostRanker] = useState(false);
  const [highlightMode, setHighlightMode] = useState<HighlightMode>('lexical');
  const [limit, setLimit] = useState(10);
  const [showSettings, setShowSettings] = useState(false);
  const settingsRef = useRef<HTMLDivElement>(null);

  // Filters
  const [yearMin, setYearMin] = useState<string>('');
  const [yearMax, setYearMax] = useState<string>('');
  const [citationMin, setCitationMin] = useState<string>('');

  // Suggested query trigger
  const pendingSuggestedSearch = useRef(false);

  // Autocomplete
  const [suggestions, setSuggestions] = useState<string[]>([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [selectedIdx, setSelectedIdx] = useState(-1);
  const debounceRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (debounceRef.current) clearTimeout(debounceRef.current);
    if (query.trim().length < 2) {
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }
    debounceRef.current = setTimeout(async () => {
      try {
        const res = await fetch('/api/autocomplete', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ query: query.trim(), limit: 5 }),
        });
        const data = await res.json();
        setSuggestions(data.titles || []);
        setShowSuggestions((data.titles || []).length > 0);
        setSelectedIdx(-1);
      } catch {
        setSuggestions([]);
      }
    }, 200);
    return () => { if (debounceRef.current) clearTimeout(debounceRef.current); };
  }, [query]);

  // Close settings popover on click outside
  useEffect(() => {
    function handleClickOutside(e: MouseEvent) {
      if (settingsRef.current && !settingsRef.current.contains(e.target as Node)) {
        setShowSettings(false);
      }
    }
    if (showSettings) {
      document.addEventListener('mousedown', handleClickOutside);
      return () => document.removeEventListener('mousedown', handleClickOutside);
    }
  }, [showSettings]);

  // Advanced time decay parameters
  const [showAdvanced, setShowAdvanced] = useState(false);
  const [origin, setOrigin] = useState(2025);
  const [offset, setOffset] = useState(5);
  const [decay, setDecay] = useState(0.8);
  const [scale, setScale] = useState(8);

  // Build Milvus filter expression from filter state
  const buildFilterExpr = useCallback(() => {
    const parts: string[] = [];
    if (yearMin) parts.push(`year >= ${yearMin}`);
    if (yearMax) parts.push(`year <= ${yearMax}`);
    if (citationMin) parts.push(`citationcount >= ${citationMin}`);
    return parts.join(' and ');
  }, [yearMin, yearMax, citationMin]);

  const handleSearch = useCallback(async () => {
    if (!query.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const filterExpr = buildFilterExpr();
      const response = await fetch('/api/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: query.trim(),
          limit,
          search_mode: searchMode,
          use_time_decay: useTimeDecay,
          use_boost: useBoost,
          use_boost_ranker: useBoostRanker,
          highlight_mode: highlightMode,
          filter: filterExpr || undefined,
          time_decay_params: useTimeDecay ? { origin, offset, decay, scale } : undefined,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Search failed');
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
    } finally {
      setLoading(false);
    }
  }, [query, limit, searchMode, useTimeDecay, useBoost, useBoostRanker, highlightMode, origin, offset, decay, scale, buildFilterExpr]);

  // Trigger search after a suggested query is clicked (query state is updated)
  useEffect(() => {
    if (pendingSuggestedSearch.current && query.trim()) {
      pendingSuggestedSearch.current = false;
      handleSearch();
    }
  }, [query, handleSearch]);

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (showSuggestions && suggestions.length > 0) {
      if (e.key === 'ArrowDown') {
        e.preventDefault();
        setSelectedIdx(prev => (prev + 1) % suggestions.length);
        return;
      }
      if (e.key === 'ArrowUp') {
        e.preventDefault();
        setSelectedIdx(prev => (prev - 1 + suggestions.length) % suggestions.length);
        return;
      }
      if (e.key === 'Enter' && selectedIdx >= 0) {
        e.preventDefault();
        setQuery(suggestions[selectedIdx]);
        setShowSuggestions(false);
        return;
      }
      if (e.key === 'Escape') {
        setShowSuggestions(false);
        return;
      }
    }
    if (e.key === 'Enter') {
      setShowSuggestions(false);
      handleSearch();
    }
  };

  const handleSearchModeChange = useCallback((mode: SearchMode) => {
    setSearchMode(mode);
  }, []);

  // Re-execute search when searchMode changes (if a query exists)
  const prevSearchModeRef = useRef(searchMode);
  useEffect(() => {
    if (prevSearchModeRef.current !== searchMode && query.trim()) {
      prevSearchModeRef.current = searchMode;
      handleSearch();
    } else {
      prevSearchModeRef.current = searchMode;
    }
  }, [searchMode, query, handleSearch]);

  // Re-execute search when filters change via chip removal (if a query exists and results are showing)
  const filterExpr = buildFilterExpr();
  const prevFilterRef = useRef(filterExpr);
  const hasResultsRef = useRef(false);
  hasResultsRef.current = results !== null;
  useEffect(() => {
    if (prevFilterRef.current !== filterExpr && query.trim() && hasResultsRef.current) {
      prevFilterRef.current = filterExpr;
      handleSearch();
    } else {
      prevFilterRef.current = filterExpr;
    }
  }, [filterExpr, query, handleSearch]);

  // Build active settings summary
  const settingsSummary: string[] = [];
  settingsSummary.push(`${limit} results`);
  if (useBoostRanker) settingsSummary.push('Boost Ranker');
  if (useTimeDecay) settingsSummary.push('Time Decay');
  if (useBoost) settingsSummary.push('Citation Boost');
  if (highlightMode === 'lexical') settingsSummary.push('Keyword highlights');

  return (
    <div className="min-h-screen bg-white">
      {/* Header */}
      <div className="bg-white border-b border-slate-200">
        <div className="max-w-6xl mx-auto px-4 pt-10 pb-8">
          <div className="text-center mb-8">
            <h1 className="text-3xl font-semibold tracking-tight text-slate-800 mb-1.5">
              Paper Search
            </h1>
            <p className="text-slate-500 text-sm">
              Semantic paper search powered by Milvus v2.6
            </p>
          </div>

          {/* Search Mode Switcher + Settings Gear */}
          <div className="flex justify-center items-center gap-2 mb-6">
            <div className="inline-flex bg-slate-100 rounded-lg p-1 gap-1">
              {([
                { mode: 'semantic' as SearchMode, label: 'Semantic', subtitle: 'Conceptual similarity' },
                { mode: 'keyword' as SearchMode, label: 'Keyword', subtitle: 'Exact term matching' },
                { mode: 'hybrid' as SearchMode, label: 'Hybrid', subtitle: 'Combined search' },
              ]).map(({ mode, label, subtitle }) => (
                <button
                  key={mode}
                  onClick={() => handleSearchModeChange(mode)}
                  className={`px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 relative ${
                    searchMode === mode
                      ? 'bg-blue-600 text-white shadow-sm'
                      : 'text-slate-600 hover:text-slate-800 hover:bg-slate-200'
                  }`}
                >
                  <span className="block">{label}</span>
                  <span className={`block text-[10px] font-normal mt-0.5 ${
                    searchMode === mode ? 'text-blue-100' : 'text-slate-400'
                  }`}>
                    {subtitle}
                  </span>
                  {mode === 'hybrid' && (
                    <span
                      className="absolute -top-2 -right-2 rounded-full text-[9px] px-1.5 py-0.5 bg-blue-50 text-blue-600 border border-blue-100 font-medium leading-none whitespace-nowrap"
                      title="Milvus v2.6 hybrid search combines dense vector and BM25 sparse retrieval in a single query"
                    >
                      v2.6
                    </span>
                  )}
                </button>
              ))}
            </div>

            {/* Settings Gear Button */}
            <div ref={settingsRef} className="relative">
              <button
                onClick={() => setShowSettings(!showSettings)}
                className={`p-2.5 rounded-lg border transition-colors duration-200 ${
                  showSettings
                    ? 'bg-slate-100 border-slate-300 text-slate-700'
                    : 'bg-white border-slate-200 text-slate-400 hover:text-slate-600 hover:border-slate-300'
                }`}
                title="Search settings"
              >
                <svg className="w-5 h-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M7.84 1.804A1 1 0 018.82 1h2.36a1 1 0 01.98.804l.331 1.652a6.993 6.993 0 011.929 1.115l1.598-.54a1 1 0 011.186.447l1.18 2.044a1 1 0 01-.205 1.251l-1.267 1.113a7.047 7.047 0 010 2.228l1.267 1.113a1 1 0 01.206 1.25l-1.18 2.045a1 1 0 01-1.187.447l-1.598-.54a6.993 6.993 0 01-1.929 1.115l-.33 1.652a1 1 0 01-.98.804H8.82a1 1 0 01-.98-.804l-.331-1.652a6.993 6.993 0 01-1.929-1.115l-1.598.54a1 1 0 01-1.186-.447l-1.18-2.044a1 1 0 01.205-1.251l1.267-1.114a7.05 7.05 0 010-2.227L1.821 7.773a1 1 0 01-.206-1.25l1.18-2.045a1 1 0 011.187-.447l1.598.54A6.993 6.993 0 017.51 3.456l.33-1.652zM10 13a3 3 0 100-6 3 3 0 000 6z" clipRule="evenodd" />
                </svg>
              </button>

              {/* Settings Popover */}
              {showSettings && (
                <div className="absolute right-0 top-full mt-2 w-72 bg-white rounded-lg border border-slate-200 shadow-lg z-30 overflow-hidden">
                  <div className="px-4 py-3 border-b border-slate-100">
                    <span className="text-sm font-semibold text-slate-700">Search Settings</span>
                  </div>
                  <div className="px-4 py-3 space-y-3">
                    {/* Ranker Toggles */}
                    <div>
                      <span className="text-xs font-medium text-slate-500 mb-2 block">Rankers</span>
                      <div className="space-y-1">
                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
                          <input
                            type="checkbox"
                            checked={useTimeDecay}
                            onChange={(e) => setUseTimeDecay(e.target.checked)}
                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
                          />
                          <div>
                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Time Decay</span>
                            <p className="text-xs text-slate-400 mt-0.5">Prefer recent papers</p>
                          </div>
                        </label>

                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
                          <input
                            type="checkbox"
                            checked={useBoost}
                            onChange={(e) => setUseBoost(e.target.checked)}
                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
                          />
                          <div>
                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Citation Boost</span>
                            <p className="text-xs text-slate-400 mt-0.5">Boost highly-cited papers</p>
                          </div>
                        </label>

                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
                          <input
                            type="checkbox"
                            checked={useBoostRanker}
                            onChange={(e) => setUseBoostRanker(e.target.checked)}
                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
                          />
                          <div>
                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Boost Ranker <span className="text-[10px] font-normal text-blue-600">v2.6</span></span>
                            <p className="text-xs text-slate-400 mt-0.5">Recency + citation boosting</p>
                          </div>
                        </label>
                      </div>
                    </div>

                    {/* Highlighting */}
                    <div className="pt-2 border-t border-slate-100">
                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Highlighting</span>
                      <div className="flex bg-slate-100 rounded-lg p-0.5">
                        <button
                          onClick={() => setHighlightMode('none')}
                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
                            highlightMode === 'none'
                              ? 'bg-white text-slate-700 shadow-sm'
                              : 'text-slate-500 hover:text-slate-700'
                          }`}
                        >
                          Off
                        </button>
                        <button
                          onClick={() => setHighlightMode('lexical')}
                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
                            highlightMode === 'lexical'
                              ? 'bg-white text-slate-700 shadow-sm'
                              : 'text-slate-500 hover:text-slate-700'
                          }`}
                        >
                          Keyword Highlights
                        </button>
                      </div>
                    </div>

                    {/* Result Limit */}
                    <div className="pt-2 border-t border-slate-100">
                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Results per search</span>
                      <select
                        value={limit}
                        onChange={(e) => setLimit(Number(e.target.value))}
                        className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                      >
                        {[5, 10, 15, 20, 25, 50].map(n => (
                          <option key={n} value={n}>{n}</option>
                        ))}
                      </select>
                    </div>

                    {/* Advanced Time Decay Settings */}
                    {useTimeDecay && (
                      <div className="pt-2 border-t border-slate-100">
                        <button
                          onClick={() => setShowAdvanced(!showAdvanced)}
                          className="text-xs text-blue-600 hover:text-blue-700 font-medium flex items-center gap-1"
                        >
                          <ChevronIcon open={showAdvanced} className="w-3 h-3" />
                          Advanced Settings
                        </button>

                        {showAdvanced && (
                          <div className="grid grid-cols-2 gap-2 mt-2 bg-slate-50 p-3 rounded-lg">
                            <div>
                              <label className="block text-xs text-slate-500 mb-1 font-medium">Origin</label>
                              <input
                                type="number"
                                value={origin}
                                onChange={(e) => setOrigin(Number(e.target.value))}
                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                              />
                            </div>
                            <div>
                              <label className="block text-xs text-slate-500 mb-1 font-medium">Offset</label>
                              <input
                                type="number"
                                value={offset}
                                onChange={(e) => setOffset(Number(e.target.value))}
                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                              />
                            </div>
                            <div>
                              <label className="block text-xs text-slate-500 mb-1 font-medium">Decay</label>
                              <input
                                type="number"
                                step="0.1"
                                min="0"
                                max="1"
                                value={decay}
                                onChange={(e) => setDecay(Number(e.target.value))}
                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                              />
                            </div>
                            <div>
                              <label className="block text-xs text-slate-500 mb-1 font-medium">Scale</label>
                              <input
                                type="number"
                                value={scale}
                                onChange={(e) => setScale(Number(e.target.value))}
                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                              />
                            </div>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Search Box */}
          <div className="relative max-w-2xl mx-auto">
            <div className="relative">
              <SearchIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400 pointer-events-none" />
              <input
                ref={inputRef}
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                onKeyDown={handleKeyDown}
                onFocus={() => suggestions.length > 0 && setShowSuggestions(true)}
                onBlur={() => setTimeout(() => setShowSuggestions(false), 150)}
                placeholder="Search for papers..."
                className="search-input w-full pl-12 pr-28 py-4 bg-white border border-slate-200 rounded-lg text-slate-700 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600 text-base shadow-sm"
                autoComplete="off"
              />
              <button
                onClick={handleSearch}
                disabled={loading || !query.trim()}
                className="absolute right-2 top-1/2 -translate-y-1/2 px-5 py-2.5 bg-blue-600 text-white rounded-md font-medium text-sm hover:bg-blue-700 disabled:bg-slate-200 disabled:text-slate-400 disabled:cursor-not-allowed"
              >
                Search
              </button>
            </div>

            {/* Autocomplete dropdown */}
            {showSuggestions && suggestions.length > 0 && (
              <ul className="absolute z-20 left-0 right-0 mt-1.5 bg-white border border-slate-200 rounded-lg shadow-md overflow-hidden custom-scrollbar max-h-64 overflow-auto">
                <li className="flex justify-end px-3 py-1 border-b border-slate-100 bg-slate-50/50">
                  <span
                    className="rounded-full text-[10px] px-2 py-0.5 bg-blue-50 text-blue-600 font-medium"
                    title="NGRAM index on the title field enables fast substring matching for instant autocomplete"
                  >
                    NGRAM Index
                  </span>
                </li>
                {suggestions.map((title, i) => {
                  const q = query.trim();
                  const idx = title.indexOf(q);
                  return (
                    <li
                      key={i}
                      className={`px-4 py-2.5 text-sm cursor-pointer border-b border-slate-100 last:border-0 flex items-center gap-3 ${
                        i === selectedIdx
                          ? 'bg-blue-50 text-blue-900'
                          : 'text-slate-700 hover:bg-slate-50'
                      }`}
                      onMouseDown={() => {
                        setQuery(title);
                        setShowSuggestions(false);
                      }}
                    >
                      <SearchIcon className={`w-3.5 h-3.5 flex-shrink-0 ${i === selectedIdx ? 'text-blue-400' : 'text-slate-300'}`} />
                      <span className="truncate">
                        {idx >= 0 ? (
                          <>
                            {title.slice(0, idx)}
                            <strong className="font-semibold text-blue-600">{title.slice(idx, idx + q.length)}</strong>
                            {title.slice(idx + q.length)}
                          </>
                        ) : title}
                      </span>
                    </li>
                  );
                })}
              </ul>
            )}
          </div>

          {/* Active settings summary */}
          <p className="text-center text-sm text-slate-500 mt-3">
            {settingsSummary.join(' · ')}
          </p>
        </div>
      </div>

      {/* Main content */}
      <div className="max-w-6xl mx-auto px-4 py-6">
        <div className="flex flex-col md:flex-row gap-6">
          {/* Filter Sidebar */}
          <aside className="w-full md:w-64 flex-shrink-0">
            <div className="bg-gray-50 rounded-lg p-4 space-y-5">
              <h3 className="text-sm font-semibold text-slate-700">Filters</h3>

              {/* Year Range */}
              <div>
                <label className="text-xs font-semibold text-slate-600 mb-2 block">Year Range</label>
                <div className="flex items-center gap-2">
                  <input
                    type="number"
                    placeholder="Min"
                    value={yearMin}
                    onChange={(e) => setYearMin(e.target.value)}
                    className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                  />
                  <span className="text-slate-400 text-xs">to</span>
                  <input
                    type="number"
                    placeholder="Max"
                    value={yearMax}
                    onChange={(e) => setYearMax(e.target.value)}
                    className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                  />
                </div>
              </div>

              {/* Citation Count */}
              <div>
                <label className="text-xs font-semibold text-slate-600 mb-2 block">Citation Count</label>
                <div className="space-y-1">
                  {[
                    { label: 'Any', value: '' },
                    { label: '10+', value: '10' },
                    { label: '100+', value: '100' },
                    { label: '500+', value: '500' },
                    { label: '1000+', value: '1000' },
                  ].map(({ label, value }) => (
                    <label key={value} className="flex items-center gap-2 cursor-pointer p-1.5 rounded-md hover:bg-slate-100 -mx-1">
                      <input
                        type="radio"
                        name="citationMin"
                        checked={citationMin === value}
                        onChange={() => setCitationMin(value)}
                        className="w-3.5 h-3.5 text-blue-600 focus:ring-blue-500"
                      />
                      <span className="text-sm text-slate-700">{label}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* Apply Filters Button */}
              <button
                onClick={() => { if (query.trim()) handleSearch(); }}
                className="w-full px-3 py-2 bg-blue-600 text-white rounded-lg text-sm font-medium hover:bg-blue-700 disabled:bg-slate-200 disabled:text-slate-400 disabled:cursor-not-allowed"
                disabled={!query.trim()}
              >
                Apply Filters
              </button>
            </div>
          </aside>

          {/* Main results area */}
          <main className="flex-1 min-w-0">
            {/* Active Filter Chips */}
            {(yearMin || yearMax || citationMin) && (
              <div className="flex flex-wrap items-center gap-2 mb-4">
                {yearMin && (
                  <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                    Year &ge; {yearMin}
                    <button onClick={() => setYearMin('')} className="ml-0.5 text-blue-400 hover:text-blue-600">&times;</button>
                  </span>
                )}
                {yearMax && (
                  <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                    Year &le; {yearMax}
                    <button onClick={() => setYearMax('')} className="ml-0.5 text-blue-400 hover:text-blue-600">&times;</button>
                  </span>
                )}
                {citationMin && (
                  <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                    Citations &ge; {citationMin}
                    <button onClick={() => setCitationMin('')} className="ml-0.5 text-blue-400 hover:text-blue-600">&times;</button>
                  </span>
                )}
                <button
                  onClick={() => { setYearMin(''); setYearMax(''); setCitationMin(''); }}
                  className="text-xs text-slate-500 hover:text-slate-700 underline ml-1"
                >
                  Clear all
                </button>
              </div>
            )}

            {/* Error Display */}
            {error && (
              <div className="flex items-start gap-3 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
                <svg className="w-5 h-5 text-red-400 flex-shrink-0 mt-0.5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-8-5a.75.75 0 01.75.75v4.5a.75.75 0 01-1.5 0v-4.5A.75.75 0 0110 5zm0 10a1 1 0 100-2 1 1 0 000 2z" clipRule="evenodd" />
                </svg>
                <p className="text-sm">{error}</p>
              </div>
            )}

            {/* Loading */}
            {loading && <LoadingSpinner />}

            {/* Results */}
            {!loading && results && (
              <div className="pb-12">
                <div className="flex items-center justify-between mb-5">
                  <div className="flex items-baseline gap-2">
                    <h2 className="text-lg font-semibold text-slate-700">
                      Results
                    </h2>
                    <span className="text-sm text-slate-400">
                      {results.papers.length} paper{results.papers.length !== 1 ? 's' : ''} for &ldquo;{results.query}&rdquo;
                    </span>
                  </div>
                  <div className="flex gap-1.5">
                    {results.options.use_time_decay && (
                      <span className="inline-flex items-center gap-1 text-xs font-medium bg-violet-50 text-violet-700 border border-violet-200 px-2.5 py-1 rounded-full">
                        <svg className="w-3 h-3" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm.75-13a.75.75 0 00-1.5 0v5c0 .414.336.75.75.75h4a.75.75 0 000-1.5h-3.25V5z" clipRule="evenodd" />
                        </svg>
                        Time Decay
                      </span>
                    )}
                    {results.options.use_boost && (
                      <span className="inline-flex items-center gap-1 text-xs font-medium bg-amber-50 text-amber-700 border border-amber-200 px-2.5 py-1 rounded-full">
                        <svg className="w-3 h-3" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M12.577 4.878a.75.75 0 01.919-.53l4.78 1.281a.75.75 0 01.531.919l-1.281 4.78a.75.75 0 01-1.449-.387l.81-3.022a19.407 19.407 0 00-5.594 5.203.75.75 0 01-1.139.093L7 10.06l-4.72 4.72a.75.75 0 01-1.06-1.06l5.25-5.25a.75.75 0 011.06 0l3.074 3.073a20.923 20.923 0 015.545-4.931l-3.042.815a.75.75 0 01-.53-.919z" clipRule="evenodd" />
                        </svg>
                        Citation Boost
                      </span>
                    )}
                    {results.options.use_boost_ranker && (
                      <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                        <svg className="w-3 h-3" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M10.868 2.884c-.321-.772-1.415-.772-1.736 0l-1.83 4.401-4.753.381c-.833.067-1.171 1.107-.536 1.651l3.62 3.102-1.106 4.637c-.194.813.691 1.456 1.405 1.02L10 15.591l4.069 2.485c.713.436 1.598-.207 1.404-1.02l-1.106-4.637 3.62-3.102c.635-.544.297-1.584-.536-1.65l-4.752-.382-1.831-4.401z" clipRule="evenodd" />
                        </svg>
                        Boost Ranker
                      </span>
                    )}
                  </div>
                </div>

                {results.papers.length === 0 ? (
                  <div className="text-center py-16">
                    <svg className="w-12 h-12 text-slate-300 mx-auto mb-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z" />
                    </svg>
                    <h3 className="text-slate-400 font-medium mb-1">No results found</h3>
                    <p className="text-sm text-slate-400 max-w-sm mx-auto">
                      Try using broader terms, different keywords, or removing some filters
                    </p>
                  </div>
                ) : (
                  <div className="space-y-3">
                    {results.papers.map((paper, idx) => (
                      <div
                        key={paper.id || `${paper.corpusid}-${idx}`}
                        className="animate-slide-up bg-white rounded-lg border border-slate-200 shadow-sm p-5 hover:shadow-md transition-shadow duration-200 group"
                        style={{ animationDelay: `${idx * 50}ms` }}
                      >
                        <div className="flex items-start justify-between gap-4">
                          <div className="flex-1 min-w-0">
                            <h3 className="font-medium text-slate-800 leading-snug mb-2.5 group-hover:text-blue-700">
                              <a
                                href={paper.url}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="hover:underline"
                              >
                                {paper.highlighted_title ? (
                                  <span dangerouslySetInnerHTML={{ __html: paper.highlighted_title }} />
                                ) : (
                                  highlightText(paper.title, results.query, highlightMode)
                                )}
                              </a>
                            </h3>
                            <div className="flex flex-wrap items-center gap-3 text-sm">
                              <span className="inline-flex items-center gap-1.5 text-slate-500">
                                <svg className="w-3.5 h-3.5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                                  <path fillRule="evenodd" d="M5.75 2a.75.75 0 01.75.75V4h7V2.75a.75.75 0 011.5 0V4h.25A2.75 2.75 0 0118 6.75v8.5A2.75 2.75 0 0115.25 18H4.75A2.75 2.75 0 012 15.25v-8.5A2.75 2.75 0 014.75 4H5V2.75A.75.75 0 015.75 2z" clipRule="evenodd" />
                                </svg>
                                {paper.year}
                              </span>
                              <span className={`inline-flex items-center gap-1.5 font-medium ${
                                paper.citationcount >= 1000
                                  ? 'text-emerald-600'
                                  : paper.citationcount >= 100
                                  ? 'text-blue-600'
                                  : 'text-slate-500'
                              }`}>
                                <svg className="w-3.5 h-3.5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                                  <path d="M3.505 2.365A41.369 41.369 0 019 2c1.863 0 3.697.124 5.495.365 1.247.167 2.18 1.108 2.435 2.268a4.45 4.45 0 00-.577-.069 43.141 43.141 0 00-4.706 0C9.229 4.696 7.5 6.727 7.5 8.998v2.24c0 1.413.67 2.735 1.76 3.562l-2.98 2.98A.75.75 0 015 17.25v-3.443c-.501-.048-1-.106-1.495-.172C2.033 13.438 1 12.162 1 10.72V5.28c0-1.441 1.033-2.717 2.505-2.914z" />
                                  <path d="M14 6c.762 0 1.52.02 2.272.062 1.057.062 1.978.93 1.978 2.11v5.064c0 1.18-.921 2.048-1.978 2.11a40 40 0 01-1.554.065c.014.158.014.318 0 .476l-.004.048c-.017.219-.04.442-.07.666-.114.867-.358 1.783-.752 2.56a.75.75 0 01-1.329-.068c-.344-.753-.558-1.503-.673-2.172a28.4 28.4 0 01-.192-1.335l-.004-.04c-.638-.198-1.22-.56-1.693-1.05V8.998c0-1.566 1.164-2.913 2.772-3.035A41.2 41.2 0 0114 6z" />
                                </svg>
                                {formatCitations(paper.citationcount)}
                              </span>
                            </div>
                          </div>
                          <span className="inline-flex items-center text-xs font-medium bg-slate-100 text-slate-600 px-2.5 py-1 rounded-full tabular-nums whitespace-nowrap mt-0.5">
                            {Math.round(paper.score * 100)}%
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* Empty state - no search yet */}
            {!loading && !results && !error && (
              <div className="text-center py-20">
                <div className="w-16 h-16 mx-auto mb-5 rounded-2xl bg-blue-50 flex items-center justify-center">
                  <SearchIcon className="w-7 h-7 text-slate-300" />
                </div>
                <h2 className="text-lg font-semibold text-slate-400 mb-1">Search academic papers</h2>
                <p className="text-sm text-slate-400 mb-6">
                  Try one of these queries:
                </p>
                <div className="flex flex-wrap justify-center gap-2">
                  {[
                    'transformer attention mechanisms',
                    'neural network pruning',
                    'large language models',
                    'graph neural networks',
                    'reinforcement learning robotics',
                  ].map((suggestion) => (
                    <button
                      key={suggestion}
                      onClick={() => {
                        pendingSuggestedSearch.current = true;
                        setQuery(suggestion);
                        setShowSuggestions(false);
                      }}
                      className="px-3.5 py-1.5 text-sm text-blue-600 bg-blue-50 border border-blue-200 rounded-full hover:bg-blue-100 transition-colors duration-200"
                    >
                      {suggestion}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </main>
        </div>

        {/* Footer */}
        <footer className="py-8 mt-8 border-t border-slate-200 text-center">
          <p className="text-xs text-slate-400">
            Powered by <span className="font-medium text-slate-500">Milvus</span> vector database
            &nbsp;&middot;&nbsp;
            <span className="font-medium text-slate-500">OpenAI</span> text-embedding-3-small
          </p>
        </footer>
      </div>
    </div>
  );
}

'use client';

import { useState, useCallback, useEffect, useRef } from 'react';

interface Paper {
  id: string;
  score: number;
  corpusid: number;
  title: string;
  highlighted_title?: string | null;
  year: number;
  citationcount: number;
  url: string;
}

interface SearchResult {
  success: boolean;
  query: string;
  papers: Paper[];
  options: {
    use_time_decay: boolean;
    use_boost: boolean;
    use_boost_ranker?: boolean;
    highlight_mode?: string;
    limit: number;
  };
}

type HighlightMode = 'none' | 'lexical';
type SearchMode = 'semantic' | 'keyword' | 'hybrid';

function highlightText(
  text: string,
  query: string,
  mode: HighlightMode
): React.ReactNode {
  if (mode === 'none' || !query.trim()) {
    return text;
  }

  const words = query.toLowerCase().split(/\s+/).filter(Boolean);

  if (mode === 'lexical') {
    const regex = new RegExp(`(${words.map(w => w.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')).join('|')})`, 'gi');
    const parts = text.split(regex);

    return parts.map((part, i) => {
      const isMatch = words.some(w => part.toLowerCase() === w);
      if (isMatch) {
        return (
          <span key={i} className="bg-yellow-200/80 px-0.5 rounded-sm">
            {part}
          </span>
        );
      }
      return part;
    });
  }

  return text;
}

function SearchIcon({ className }: { className?: string }) {
  return (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
      <path fillRule="evenodd" d="M9 3.5a5.5 5.5 0 100 11 5.5 5.5 0 000-11zM2 9a7 7 0 1112.452 4.391l3.328 3.329a.75.75 0 11-1.06 1.06l-3.329-3.328A7 7 0 012 9z" clipRule="evenodd" />
    </svg>
  );
}

function ChevronIcon({ open, className }: { open: boolean; className?: string }) {
  return (
    <svg
      className={`${className} transition-transform duration-200 ${open ? 'rotate-180' : ''}`}
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 20 20"
      fill="currentColor"
    >
      <path fillRule="evenodd" d="M5.22 8.22a.75.75 0 0 1 1.06 0L10 11.94l3.72-3.72a.75.75 0 1 1 1.06 1.06l-4.25 4.25a.75.75 0 0 1-1.06 0L5.22 9.28a.75.75 0 0 1 0-1.06Z" clipRule="evenodd" />
    </svg>
  );
}

function LoadingSpinner() {
  return (
    <div className="flex flex-col items-center justify-center py-16 gap-4">
      <div className="relative">
        <div className="w-10 h-10 border-3 border-blue-200 rounded-full" />
        <div className="absolute top-0 left-0 w-10 h-10 border-3 border-transparent border-t-blue-600 rounded-full animate-spin-slow" />
      </div>
      <div className="flex gap-1.5">
        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" />
        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.2s' }} />
        <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-pulse-dot" style={{ animationDelay: '0.4s' }} />
      </div>
      <p className="text-sm text-slate-400 font-medium">Searching papers...</p>
    </div>
  );
}

function formatCitations(count: number): string {
  if (count >= 1000) {
    return `${(count / 1000).toFixed(count >= 10000 ? 0 : 1)}k`;
  }
  return count.toLocaleString();
}

export default function Home() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<SearchResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Search mode
  const [searchMode, setSearchMode] = useState<SearchMode>('hybrid');

  // Options
  const [useTimeDecay, setUseTimeDecay] = useState(false);
  const [useBoost, setUseBoost] = useState(false);
  const [useBoostRanker, setUseBoostRanker] = useState(false);
  const [highlightMode, setHighlightMode] = useState<HighlightMode>('lexical');
  const [limit, setLimit] = useState(10);
  const [showSettings, setShowSettings] = useState(false);
  const settingsRef = useRef<HTMLDivElement>(null);

  // Filters
  const [yearMin, setYearMin] = useState<string>('');
  const [yearMax, setYearMax] = useState<string>('');
  const [citationMin, setCitationMin] = useState<string>('');

  // Suggested query trigger
  const pendingSuggestedSearch = useRef(false);

  // Autocomplete
  const [suggestions, setSuggestions] = useState<string[]>([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [selectedIdx, setSelectedIdx] = useState(-1);
  const debounceRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (debounceRef.current) clearTimeout(debounceRef.current);
    if (query.trim().length < 2) {
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }
    debounceRef.current = setTimeout(async () => {
      try {
        const res = await fetch('/api/autocomplete', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ query: query.trim(), limit: 5 }),
        });
        const data = await res.json();
        setSuggestions(data.titles || []);
        setShowSuggestions((data.titles || []).length > 0);
        setSelectedIdx(-1);
      } catch {
        setSuggestions([]);
      }
    }, 200);
    return () => { if (debounceRef.current) clearTimeout(debounceRef.current); };
  }, [query]);

  // Close settings popover on click outside
  useEffect(() => {
    function handleClickOutside(e: MouseEvent) {
      if (settingsRef.current && !settingsRef.current.contains(e.target as Node)) {
        setShowSettings(false);
      }
    }
    if (showSettings) {
      document.addEventListener('mousedown', handleClickOutside);
      return () => document.removeEventListener('mousedown', handleClickOutside);
    }
  }, [showSettings]);

  // Advanced time decay parameters
  const [showAdvanced, setShowAdvanced] = useState(false);
  const [origin, setOrigin] = useState(2025);
  const [offset, setOffset] = useState(5);
  const [decay, setDecay] = useState(0.8);
  const [scale, setScale] = useState(8);

  // Build Milvus filter expression from filter state
  const buildFilterExpr = useCallback(() => {
    const parts: string[] = [];
    if (yearMin) parts.push(`year >= ${yearMin}`);
    if (yearMax) parts.push(`year <= ${yearMax}`);
    if (citationMin) parts.push(`citationcount >= ${citationMin}`);
    return parts.join(' and ');
  }, [yearMin, yearMax, citationMin]);

  const handleSearch = useCallback(async () => {
    if (!query.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const filterExpr = buildFilterExpr();
      const response = await fetch('/api/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: query.trim(),
          limit,
          search_mode: searchMode,
          use_time_decay: useTimeDecay,
          use_boost: useBoost,
          use_boost_ranker: useBoostRanker,
          highlight_mode: highlightMode,
          filter: filterExpr || undefined,
          time_decay_params: useTimeDecay ? { origin, offset, decay, scale } : undefined,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Search failed');
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
    } finally {
      setLoading(false);
    }
  }, [query, limit, searchMode, useTimeDecay, useBoost, useBoostRanker, highlightMode, origin, offset, decay, scale, buildFilterExpr]);

  // Trigger search after a suggested query is clicked (query state is updated)
  useEffect(() => {
    if (pendingSuggestedSearch.current && query.trim()) {
      pendingSuggestedSearch.current = false;
      handleSearch();
    }
  }, [query, handleSearch]);

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (showSuggestions && suggestions.length > 0) {
      if (e.key === 'ArrowDown') {
        e.preventDefault();
        setSelectedIdx(prev => (prev + 1) % suggestions.length);
        return;
      }
      if (e.key === 'ArrowUp') {
        e.preventDefault();
        setSelectedIdx(prev => (prev - 1 + suggestions.length) % suggestions.length);
        return;
      }
      if (e.key === 'Enter' && selectedIdx >= 0) {
        e.preventDefault();
        setQuery(suggestions[selectedIdx]);
        setShowSuggestions(false);
        return;
      }
      if (e.key === 'Escape') {
        setShowSuggestions(false);
        return;
      }
    }
    if (e.key === 'Enter') {
      setShowSuggestions(false);
      handleSearch();
    }
  };

  const handleSearchModeChange = useCallback((mode: SearchMode) => {
    setSearchMode(mode);
  }, []);

  // Re-execute search when searchMode changes (if a query exists)
  const prevSearchModeRef = useRef(searchMode);
  useEffect(() => {
    if (prevSearchModeRef.current !== searchMode && query.trim()) {
      prevSearchModeRef.current = searchMode;
      handleSearch();
    } else {
      prevSearchModeRef.current = searchMode;
    }
  }, [searchMode, query, handleSearch]);

  // Re-execute search when filters change via chip removal (if a query exists and results are showing)
  const filterExpr = buildFilterExpr();
  const prevFilterRef = useRef(filterExpr);
  const hasResultsRef = useRef(false);
  hasResultsRef.current = results !== null;
  useEffect(() => {
    if (prevFilterRef.current !== filterExpr && query.trim() && hasResultsRef.current) {
      prevFilterRef.current = filterExpr;
      handleSearch();
    } else {
      prevFilterRef.current = filterExpr;
    }
  }, [filterExpr, query, handleSearch]);

  // Build active settings summary
  const settingsSummary: string[] = [];
  settingsSummary.push(`${limit} results`);
  if (useBoostRanker) settingsSummary.push('Boost Ranker');
  if (useTimeDecay) settingsSummary.push('Time Decay');
  if (useBoost) settingsSummary.push('Citation Boost');
  if (highlightMode === 'lexical') settingsSummary.push('Keyword highlights');

  return (
    <div className="min-h-screen bg-white">
      {/* Header */}
      <div className="bg-white border-b border-slate-200">
        <div className="max-w-6xl mx-auto px-4 pt-10 pb-8">
          <div className="text-center mb-8">
            <h1 className="text-3xl font-semibold tracking-tight text-slate-800 mb-1.5">
              Paper Search
            </h1>
            <p className="text-slate-500 text-sm">
              Semantic paper search powered by Milvus v2.6
            </p>
          </div>

          {/* Search Mode Switcher + Settings Gear */}
          <div className="flex justify-center items-center gap-2 mb-6">
            <div className="inline-flex bg-slate-100 rounded-lg p-1 gap-1">
              {([
                { mode: 'semantic' as SearchMode, label: 'Semantic', subtitle: 'Conceptual similarity' },
                { mode: 'keyword' as SearchMode, label: 'Keyword', subtitle: 'Exact term matching' },
                { mode: 'hybrid' as SearchMode, label: 'Hybrid', subtitle: 'Combined search' },
              ]).map(({ mode, label, subtitle }) => (
                <button
                  key={mode}
                  onClick={() => handleSearchModeChange(mode)}
                  className={`px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 relative ${
                    searchMode === mode
                      ? 'bg-blue-600 text-white shadow-sm'
                      : 'text-slate-600 hover:text-slate-800 hover:bg-slate-200'
                  }`}
                >
                  <span className="block">{label}</span>
                  <span className={`block text-[10px] font-normal mt-0.5 ${
                    searchMode === mode ? 'text-blue-100' : 'text-slate-400'
                  }`}>
                    {subtitle}
                  </span>
                  {mode === 'hybrid' && (
                    <span
                      className="absolute -top-2 -right-2 rounded-full text-[9px] px-1.5 py-0.5 bg-blue-50 text-blue-600 border border-blue-100 font-medium leading-none whitespace-nowrap"
                      title="Milvus v2.6 hybrid search combines dense vector and BM25 sparse retrieval in a single query"
                    >
                      v2.6
                    </span>
                  )}
                </button>
              ))}
            </div>

            {/* Settings Gear Button */}
            <div ref={settingsRef} className="relative">
              <button
                onClick={() => setShowSettings(!showSettings)}
                className={`p-2.5 rounded-lg border transition-colors duration-200 ${
                  showSettings
                    ? 'bg-slate-100 border-slate-300 text-slate-700'
                    : 'bg-white border-slate-200 text-slate-400 hover:text-slate-600 hover:border-slate-300'
                }`}
                title="Search settings"
              >
                <svg className="w-5 h-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M7.84 1.804A1 1 0 018.82 1h2.36a1 1 0 01.98.804l.331 1.652a6.993 6.993 0 011.929 1.115l1.598-.54a1 1 0 011.186.447l1.18 2.044a1 1 0 01-.205 1.251l-1.267 1.113a7.047 7.047 0 010 2.228l1.267 1.113a1 1 0 01.206 1.25l-1.18 2.045a1 1 0 01-1.187.447l-1.598-.54a6.993 6.993 0 01-1.929 1.115l-.33 1.652a1 1 0 01-.98.804H8.82a1 1 0 01-.98-.804l-.331-1.652a6.993 6.993 0 01-1.929-1.115l-1.598.54a1 1 0 01-1.186-.447l-1.18-2.044a1 1 0 01.205-1.251l1.267-1.114a7.05 7.05 0 010-2.227L1.821 7.773a1 1 0 01-.206-1.25l1.18-2.045a1 1 0 011.187-.447l1.598.54A6.993 6.993 0 017.51 3.456l.33-1.652zM10 13a3 3 0 100-6 3 3 0 000 6z" clipRule="evenodd" />
                </svg>
              </button>

              {/* Settings Popover */}
              {showSettings && (
                <div className="absolute right-0 top-full mt-2 w-72 bg-white rounded-lg border border-slate-200 shadow-lg z-30 overflow-hidden">
                  <div className="px-4 py-3 border-b border-slate-100">
                    <span className="text-sm font-semibold text-slate-700">Search Settings</span>
                  </div>
                  <div className="px-4 py-3 space-y-3">
                    {/* Ranker Toggles */}
                    <div>
                      <span className="text-xs font-medium text-slate-500 mb-2 block">Rankers</span>
                      <div className="space-y-1">
                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
                          <input
                            type="checkbox"
                            checked={useTimeDecay}
                            onChange={(e) => setUseTimeDecay(e.target.checked)}
                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
                          />
                          <div>
                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Time Decay</span>
                            <p className="text-xs text-slate-400 mt-0.5">Prefer recent papers</p>
                          </div>
                        </label>

                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
                          <input
                            type="checkbox"
                            checked={useBoost}
                            onChange={(e) => setUseBoost(e.target.checked)}
                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
                          />
                          <div>
                            <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Citation Boost</span>
                            <p className="text-xs text-slate-400 mt-0.5">Boost highly-cited papers</p>
                          </div>
                        </label>

                        <label className="flex items-start gap-3 cursor-pointer group p-2 rounded-lg hover:bg-slate-50 -mx-1">
                          <input
                            type="checkbox"
                            checked={useBoostRanker}
                            onChange={(e) => setUseBoostRanker(e.target.checked)}
                            className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500 mt-0.5"
                          />
                          <div className="flex-1">
                            <div className="flex items-center gap-2">
                              <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">Boost Ranker</span>
                              <span
                                className="rounded-full text-[10px] px-2 py-0.5 bg-blue-50 text-blue-600 font-medium"
                                title="Milvus v2.6 Boost Ranker applies metadata-driven score boosting for recency and citation count"
                              >
                                v2.6 Boost Ranker
                              </span>
                            </div>
                            <p className="text-xs text-slate-400 mt-0.5">Recency + citation boosting</p>
                          </div>
                        </label>
                      </div>
                    </div>

                    {/* Highlighting */}
                    <div className="pt-2 border-t border-slate-100">
                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Highlighting</span>
                      <div className="flex bg-slate-100 rounded-lg p-0.5">
                        <button
                          onClick={() => setHighlightMode('none')}
                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
                            highlightMode === 'none'
                              ? 'bg-white text-slate-700 shadow-sm'
                              : 'text-slate-500 hover:text-slate-700'
                          }`}
                        >
                          Off
                        </button>
                        <button
                          onClick={() => setHighlightMode('lexical')}
                          className={`flex-1 px-3 py-1 text-xs font-medium rounded-md ${
                            highlightMode === 'lexical'
                              ? 'bg-white text-slate-700 shadow-sm'
                              : 'text-slate-500 hover:text-slate-700'
                          }`}
                        >
                          Keyword Highlights
                        </button>
                      </div>
                    </div>

                    {/* Result Limit */}
                    <div className="pt-2 border-t border-slate-100">
                      <span className="text-xs font-medium text-slate-500 mb-1.5 block">Results per search</span>
                      <select
                        value={limit}
                        onChange={(e) => setLimit(Number(e.target.value))}
                        className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                      >
                        {[5, 10, 15, 20, 25, 50].map(n => (
                          <option key={n} value={n}>{n}</option>
                        ))}
                      </select>
                    </div>

                    {/* Advanced Time Decay Settings */}
                    {useTimeDecay && (
                      <div className="pt-2 border-t border-slate-100">
                        <button
                          onClick={() => setShowAdvanced(!showAdvanced)}
                          className="text-xs text-blue-600 hover:text-blue-700 font-medium flex items-center gap-1"
                        >
                          <ChevronIcon open={showAdvanced} className="w-3 h-3" />
                          Advanced Settings
                        </button>

                        {showAdvanced && (
                          <div className="grid grid-cols-2 gap-2 mt-2 bg-slate-50 p-3 rounded-lg">
                            <div>
                              <label className="block text-xs text-slate-500 mb-1 font-medium">Origin</label>
                              <input
                                type="number"
                                value={origin}
                                onChange={(e) => setOrigin(Number(e.target.value))}
                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                              />
                            </div>
                            <div>
                              <label className="block text-xs text-slate-500 mb-1 font-medium">Offset</label>
                              <input
                                type="number"
                                value={offset}
                                onChange={(e) => setOffset(Number(e.target.value))}
                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                              />
                            </div>
                            <div>
                              <label className="block text-xs text-slate-500 mb-1 font-medium">Decay</label>
                              <input
                                type="number"
                                step="0.1"
                                min="0"
                                max="1"
                                value={decay}
                                onChange={(e) => setDecay(Number(e.target.value))}
                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                              />
                            </div>
                            <div>
                              <label className="block text-xs text-slate-500 mb-1 font-medium">Scale</label>
                              <input
                                type="number"
                                value={scale}
                                onChange={(e) => setScale(Number(e.target.value))}
                                className="w-full px-2 py-1.5 border border-slate-200 rounded-lg text-sm bg-white focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                              />
                            </div>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Search Box */}
          <div className="relative max-w-2xl mx-auto">
            <div className="relative">
              <SearchIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400 pointer-events-none" />
              <input
                ref={inputRef}
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                onKeyDown={handleKeyDown}
                onFocus={() => suggestions.length > 0 && setShowSuggestions(true)}
                onBlur={() => setTimeout(() => setShowSuggestions(false), 150)}
                placeholder="Search for papers..."
                className="search-input w-full pl-12 pr-28 py-4 bg-white border border-slate-200 rounded-lg text-slate-700 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600 text-base shadow-sm"
                autoComplete="off"
              />
              <button
                onClick={handleSearch}
                disabled={loading || !query.trim()}
                className="absolute right-2 top-1/2 -translate-y-1/2 px-5 py-2.5 bg-blue-600 text-white rounded-md font-medium text-sm hover:bg-blue-700 disabled:bg-slate-200 disabled:text-slate-400 disabled:cursor-not-allowed"
              >
                Search
              </button>
            </div>

            {/* Autocomplete dropdown */}
            {showSuggestions && suggestions.length > 0 && (
              <ul className="absolute z-20 left-0 right-0 mt-1.5 bg-white border border-slate-200 rounded-lg shadow-md overflow-hidden custom-scrollbar max-h-64 overflow-auto">
                <li className="flex justify-end px-3 py-1 border-b border-slate-100 bg-slate-50/50">
                  <span
                    className="rounded-full text-[10px] px-2 py-0.5 bg-blue-50 text-blue-600 font-medium"
                    title="NGRAM index on the title field enables fast substring matching for instant autocomplete"
                  >
                    NGRAM Index
                  </span>
                </li>
                {suggestions.map((title, i) => {
                  const q = query.trim();
                  const idx = title.indexOf(q);
                  return (
                    <li
                      key={i}
                      className={`px-4 py-2.5 text-sm cursor-pointer border-b border-slate-100 last:border-0 flex items-center gap-3 ${
                        i === selectedIdx
                          ? 'bg-blue-50 text-blue-900'
                          : 'text-slate-700 hover:bg-slate-50'
                      }`}
                      onMouseDown={() => {
                        setQuery(title);
                        setShowSuggestions(false);
                      }}
                    >
                      <SearchIcon className={`w-3.5 h-3.5 flex-shrink-0 ${i === selectedIdx ? 'text-blue-400' : 'text-slate-300'}`} />
                      <span className="truncate">
                        {idx >= 0 ? (
                          <>
                            {title.slice(0, idx)}
                            <strong className="font-semibold text-blue-600">{title.slice(idx, idx + q.length)}</strong>
                            {title.slice(idx + q.length)}
                          </>
                        ) : title}
                      </span>
                    </li>
                  );
                })}
              </ul>
            )}
          </div>

          {/* Active settings summary */}
          <p className="text-center text-sm text-slate-500 mt-3">
            {settingsSummary.join(' · ')}
          </p>
        </div>
      </div>

      {/* Main content */}
      <div className="max-w-6xl mx-auto px-4 py-6">
        <div className="flex flex-col md:flex-row gap-6">
          {/* Filter Sidebar */}
          <aside className="w-full md:w-64 flex-shrink-0">
            <div className="bg-gray-50 rounded-lg p-4 space-y-5">
              <h3 className="text-sm font-semibold text-slate-700">Filters</h3>

              {/* Year Range */}
              <div>
                <label className="text-xs font-semibold text-slate-600 mb-2 block">Year Range</label>
                <div className="flex items-center gap-2">
                  <input
                    type="number"
                    placeholder="Min"
                    value={yearMin}
                    onChange={(e) => setYearMin(e.target.value)}
                    className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                  />
                  <span className="text-slate-400 text-xs">to</span>
                  <input
                    type="number"
                    placeholder="Max"
                    value={yearMax}
                    onChange={(e) => setYearMax(e.target.value)}
                    className="w-full px-2.5 py-1.5 border border-slate-200 rounded-lg text-sm text-slate-700 bg-white focus:outline-none focus:ring-2 focus:ring-blue-600/30 focus:border-blue-600"
                  />
                </div>
              </div>

              {/* Citation Count */}
              <div>
                <label className="text-xs font-semibold text-slate-600 mb-2 block">Citation Count</label>
                <div className="space-y-1">
                  {[
                    { label: 'Any', value: '' },
                    { label: '10+', value: '10' },
                    { label: '100+', value: '100' },
                    { label: '500+', value: '500' },
                    { label: '1000+', value: '1000' },
                  ].map(({ label, value }) => (
                    <label key={value} className="flex items-center gap-2 cursor-pointer p-1.5 rounded-md hover:bg-slate-100 -mx-1">
                      <input
                        type="radio"
                        name="citationMin"
                        checked={citationMin === value}
                        onChange={() => setCitationMin(value)}
                        className="w-3.5 h-3.5 text-blue-600 focus:ring-blue-500"
                      />
                      <span className="text-sm text-slate-700">{label}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* Apply Filters Button */}
              <button
                onClick={() => { if (query.trim()) handleSearch(); }}
                className="w-full px-3 py-2 bg-blue-600 text-white rounded-lg text-sm font-medium hover:bg-blue-700 disabled:bg-slate-200 disabled:text-slate-400 disabled:cursor-not-allowed"
                disabled={!query.trim()}
              >
                Apply Filters
              </button>
            </div>
          </aside>

          {/* Main results area */}
          <main className="flex-1 min-w-0">
            {/* Active Filter Chips */}
            {(yearMin || yearMax || citationMin) && (
              <div className="flex flex-wrap items-center gap-2 mb-4">
                {yearMin && (
                  <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                    Year &ge; {yearMin}
                    <button onClick={() => setYearMin('')} className="ml-0.5 text-blue-400 hover:text-blue-600">&times;</button>
                  </span>
                )}
                {yearMax && (
                  <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                    Year &le; {yearMax}
                    <button onClick={() => setYearMax('')} className="ml-0.5 text-blue-400 hover:text-blue-600">&times;</button>
                  </span>
                )}
                {citationMin && (
                  <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                    Citations &ge; {citationMin}
                    <button onClick={() => setCitationMin('')} className="ml-0.5 text-blue-400 hover:text-blue-600">&times;</button>
                  </span>
                )}
                <button
                  onClick={() => { setYearMin(''); setYearMax(''); setCitationMin(''); }}
                  className="text-xs text-slate-500 hover:text-slate-700 underline ml-1"
                >
                  Clear all
                </button>
              </div>
            )}

            {/* Error Display */}
            {error && (
              <div className="flex items-start gap-3 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
                <svg className="w-5 h-5 text-red-400 flex-shrink-0 mt-0.5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-8-5a.75.75 0 01.75.75v4.5a.75.75 0 01-1.5 0v-4.5A.75.75 0 0110 5zm0 10a1 1 0 100-2 1 1 0 000 2z" clipRule="evenodd" />
                </svg>
                <p className="text-sm">{error}</p>
              </div>
            )}

            {/* Loading */}
            {loading && <LoadingSpinner />}

            {/* Results */}
            {!loading && results && (
              <div className="pb-12">
                <div className="flex items-center justify-between mb-5">
                  <div className="flex items-baseline gap-2">
                    <h2 className="text-lg font-semibold text-slate-700">
                      Results
                    </h2>
                    <span className="text-sm text-slate-400">
                      {results.papers.length} paper{results.papers.length !== 1 ? 's' : ''} for &ldquo;{results.query}&rdquo;
                    </span>
                  </div>
                  <div className="flex gap-1.5">
                    {results.options.use_time_decay && (
                      <span className="inline-flex items-center gap-1 text-xs font-medium bg-violet-50 text-violet-700 border border-violet-200 px-2.5 py-1 rounded-full">
                        <svg className="w-3 h-3" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm.75-13a.75.75 0 00-1.5 0v5c0 .414.336.75.75.75h4a.75.75 0 000-1.5h-3.25V5z" clipRule="evenodd" />
                        </svg>
                        Time Decay
                      </span>
                    )}
                    {results.options.use_boost && (
                      <span className="inline-flex items-center gap-1 text-xs font-medium bg-amber-50 text-amber-700 border border-amber-200 px-2.5 py-1 rounded-full">
                        <svg className="w-3 h-3" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M12.577 4.878a.75.75 0 01.919-.53l4.78 1.281a.75.75 0 01.531.919l-1.281 4.78a.75.75 0 01-1.449-.387l.81-3.022a19.407 19.407 0 00-5.594 5.203.75.75 0 01-1.139.093L7 10.06l-4.72 4.72a.75.75 0 01-1.06-1.06l5.25-5.25a.75.75 0 011.06 0l3.074 3.073a20.923 20.923 0 015.545-4.931l-3.042.815a.75.75 0 01-.53-.919z" clipRule="evenodd" />
                        </svg>
                        Citation Boost
                      </span>
                    )}
                    {results.options.use_boost_ranker && (
                      <span className="inline-flex items-center gap-1 text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200 px-2.5 py-1 rounded-full">
                        <svg className="w-3 h-3" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M10.868 2.884c-.321-.772-1.415-.772-1.736 0l-1.83 4.401-4.753.381c-.833.067-1.171 1.107-.536 1.651l3.62 3.102-1.106 4.637c-.194.813.691 1.456 1.405 1.02L10 15.591l4.069 2.485c.713.436 1.598-.207 1.404-1.02l-1.106-4.637 3.62-3.102c.635-.544.297-1.584-.536-1.65l-4.752-.382-1.831-4.401z" clipRule="evenodd" />
                        </svg>
                        Boost Ranker
                      </span>
                    )}
                  </div>
                </div>

                {results.papers.length === 0 ? (
                  <div className="text-center py-16">
                    <svg className="w-12 h-12 text-slate-300 mx-auto mb-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z" />
                    </svg>
                    <h3 className="text-slate-400 font-medium mb-1">No results found</h3>
                    <p className="text-sm text-slate-400 max-w-sm mx-auto">
                      Try using broader terms, different keywords, or removing some filters
                    </p>
                  </div>
                ) : (
                  <div className="space-y-3">
                    {results.papers.map((paper, idx) => (
                      <div
                        key={paper.id || `${paper.corpusid}-${idx}`}
                        className="animate-slide-up bg-white rounded-lg border border-slate-200 shadow-sm p-5 hover:shadow-md transition-shadow duration-200 group"
                        style={{ animationDelay: `${idx * 50}ms` }}
                      >
                        <div className="flex items-start justify-between gap-4">
                          <div className="flex-1 min-w-0">
                            <h3 className="font-medium text-slate-800 leading-snug mb-2.5 group-hover:text-blue-700">
                              <a
                                href={paper.url}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="hover:underline"
                              >
                                {paper.highlighted_title ? (
                                  <span dangerouslySetInnerHTML={{ __html: paper.highlighted_title }} />
                                ) : (
                                  highlightText(paper.title, results.query, highlightMode)
                                )}
                              </a>
                            </h3>
                            <div className="flex flex-wrap items-center gap-3 text-sm">
                              <span className="inline-flex items-center gap-1.5 text-slate-500">
                                <svg className="w-3.5 h-3.5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                                  <path fillRule="evenodd" d="M5.75 2a.75.75 0 01.75.75V4h7V2.75a.75.75 0 011.5 0V4h.25A2.75 2.75 0 0118 6.75v8.5A2.75 2.75 0 0115.25 18H4.75A2.75 2.75 0 012 15.25v-8.5A2.75 2.75 0 014.75 4H5V2.75A.75.75 0 015.75 2z" clipRule="evenodd" />
                                </svg>
                                {paper.year}
                              </span>
                              <span className={`inline-flex items-center gap-1.5 font-medium ${
                                paper.citationcount >= 1000
                                  ? 'text-emerald-600'
                                  : paper.citationcount >= 100
                                  ? 'text-blue-600'
                                  : 'text-slate-500'
                              }`}>
                                <svg className="w-3.5 h-3.5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                                  <path d="M3.505 2.365A41.369 41.369 0 019 2c1.863 0 3.697.124 5.495.365 1.247.167 2.18 1.108 2.435 2.268a4.45 4.45 0 00-.577-.069 43.141 43.141 0 00-4.706 0C9.229 4.696 7.5 6.727 7.5 8.998v2.24c0 1.413.67 2.735 1.76 3.562l-2.98 2.98A.75.75 0 015 17.25v-3.443c-.501-.048-1-.106-1.495-.172C2.033 13.438 1 12.162 1 10.72V5.28c0-1.441 1.033-2.717 2.505-2.914z" />
                                  <path d="M14 6c.762 0 1.52.02 2.272.062 1.057.062 1.978.93 1.978 2.11v5.064c0 1.18-.921 2.048-1.978 2.11a40 40 0 01-1.554.065c.014.158.014.318 0 .476l-.004.048c-.017.219-.04.442-.07.666-.114.867-.358 1.783-.752 2.56a.75.75 0 01-1.329-.068c-.344-.753-.558-1.503-.673-2.172a28.4 28.4 0 01-.192-1.335l-.004-.04c-.638-.198-1.22-.56-1.693-1.05V8.998c0-1.566 1.164-2.913 2.772-3.035A41.2 41.2 0 0114 6z" />
                                </svg>
                                {formatCitations(paper.citationcount)}
                              </span>
                            </div>
                          </div>
                          <span className="inline-flex items-center text-xs font-medium bg-slate-100 text-slate-600 px-2.5 py-1 rounded-full tabular-nums whitespace-nowrap mt-0.5">
                            {Math.round(paper.score * 100)}%
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* Empty state - no search yet */}
            {!loading && !results && !error && (
              <div className="text-center py-20">
                <div className="w-16 h-16 mx-auto mb-5 rounded-2xl bg-blue-50 flex items-center justify-center">
                  <SearchIcon className="w-7 h-7 text-slate-300" />
                </div>
                <h2 className="text-lg font-semibold text-slate-400 mb-1">Search academic papers</h2>
                <p className="text-sm text-slate-400 mb-6">
                  Try one of these queries:
                </p>
                <div className="flex flex-wrap justify-center gap-2">
                  {[
                    'transformer attention mechanisms',
                    'neural network pruning',
                    'large language models',
                    'graph neural networks',
                    'reinforcement learning robotics',
                  ].map((suggestion) => (
                    <button
                      key={suggestion}
                      onClick={() => {
                        pendingSuggestedSearch.current = true;
                        setQuery(suggestion);
                        setShowSuggestions(false);
                      }}
                      className="px-3.5 py-1.5 text-sm text-blue-600 bg-blue-50 border border-blue-200 rounded-full hover:bg-blue-100 transition-colors duration-200"
                    >
                      {suggestion}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </main>
        </div>

        {/* Footer */}
        <footer className="py-8 mt-8 border-t border-slate-200 text-center">
          <p className="text-xs text-slate-400">
            Powered by <span className="font-medium text-slate-500">Milvus</span> vector database
            &nbsp;&middot;&nbsp;
            <span className="font-medium text-slate-500">OpenAI</span> text-embedding-3-small
          </p>
        </footer>
      </div>
    </div>
  );
}

content.content:
{
  "dev": "next dev",
  "build": "next build",
  "start": "next start",
  "lint": "eslint"
}
